      // Create client
      const [newClient] = await db
        .insert(clients)
        .values({
          name,
          subdomain,
          email,
          status: 'trial',
          trialEndsAt,
          settings: JSON.stringify({
            planId: plan.id,
            planName: plan.name,
            maxUsers: plan.maxUsers || 10,
            maxStorage: plan.maxStorageGB || 1
          })
        })
        .returning();

      // Create trial subscription
      await db
        .insert(subscriptions)
        .values({
          clientId: newClient.id,
          planId: plan.id,
          planName: plan.name,
          plan: plan.name.toLowerCase() as any,
          amount: '0',
          paymentStatus: 'paid',
          startDate: new Date(),
          endDate: trialEndsAt
        });

      // Broadcast real-time update
      realtimeService.broadcast({
        resource: 'saas-clients',
        action: 'create',
        data: newClient
      });

      res.json({
        message: 'Client created successfully',
        client: newClient,
        trialUrl: `https://${subdomain}.profesionalservis.my.id`
      });
    } catch (error) {
      console.error('Error creating SaaS client:', error);
      res.status(500).json({ message: 'Failed to create client' });
    }
  });

  // Update client status
  app.patch('/api/admin/saas/clients/:id/status', isAuthenticated, requirePermission('saas_admin'), async (req: any, res) => {
    try {
      const { id } = req.params;
      const { status } = req.body;

      if (!['active', 'suspended', 'expired', 'trial'].includes(status)) {
        return res.status(400).json({ message: 'Invalid status' });
      }

      const [updatedClient] = await db
        .update(clients)
        .set({ 
          status,
          updatedAt: new Date()
        })
        .where(eq(clients.id, id))
        .returning();

      if (!updatedClient) {
        return res.status(404).json({ message: 'Client not found' });
      }

      // Broadcast real-time update
      realtimeService.broadcast({
        resource: 'saas-clients',
        action: 'update',
        data: updatedClient,
        id
      });

      res.json({
        message: 'Client status updated successfully',
        client: updatedClient
      });
    } catch (error) {
      console.error('Error updating client status:', error);
      res.status(500).json({ message: 'Failed to update client status' });
    }
  });

  // SaaS Dashboard Stats
  app.get('/api/admin/saas/stats', isAuthenticated, requirePermission('saas_admin'), async (req, res) => {
    try {
      // Total clients
      const [totalClientsResult] = await db
        .select({ count: count() })
        .from(clients);
      const totalClients = totalClientsResult.count;

      // Active clients
      const [activeClientsResult] = await db
        .select({ count: count() })
        .from(clients)
        .where(eq(clients.status, 'active'));
      const activeClients = activeClientsResult.count;

      // Trial clients
      const [trialClientsResult] = await db
        .select({ count: count() })
        .from(clients)
        .where(eq(clients.status, 'trial'));
      const trialClients = trialClientsResult.count;

      // New clients this month
      const currentDate = new Date();
      const firstDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
      const [newClientsResult] = await db
        .select({ count: count() })
        .from(clients)
        .where(gte(clients.createdAt, firstDayOfMonth));
      const newClientsThisMonth = newClientsResult.count;

      // Revenue calculation (mock for now)
      const monthlyRevenue = activeClients * 199000; // Assuming average Rp 199k per client

      // Expiring trials (trials ending in next 7 days)
      const nextWeek = new Date();
      nextWeek.setDate(nextWeek.getDate() + 7);
      const [expiringTrialsResult] = await db
        .select({ count: count() })
        .from(clients)
        .where(
          and(
            eq(clients.status, 'trial'),
            lte(clients.trialEndsAt, nextWeek)
          )
        );
      const expiringTrials = expiringTrialsResult.count;

      res.json({
        totalClients,
        activeClients,
        trialClients,
        newClientsThisMonth,
        monthlyRevenue,
        revenueGrowth: 15, // Mock growth percentage
        expiringTrials,
        averageRevenuePerClient: Math.round(monthlyRevenue / Math.max(activeClients, 1))
      });
    } catch (error) {
      console.error('Error fetching SaaS stats:', error);
      res.status(500).json({ message: 'Failed to fetch SaaS dashboard stats' });
    }
  });

  // Plan Management
  app.get('/api/admin/saas/plans', isAuthenticated, requirePermission('saas_admin'), async (req, res) => {
    try {
      const allPlans = await db
        .select()
        .from(plans)
        .orderBy(plans.price);

      res.json(allPlans);
    } catch (error) {
      console.error('Error fetching plans:', error);
      res.status(500).json({ message: 'Failed to fetch plans' });
    }
  });

  // Update plan
  app.put('/api/admin/saas/plans/:id', isAuthenticated, requirePermission('saas_admin'), async (req: any, res) => {
    try {
      const { id } = req.params;
      const planData = req.body;

      const [updatedPlan] = await db
        .update(plans)
        .set({ 
          ...planData,
          updatedAt: new Date()
        })
        .where(eq(plans.id, id))
        .returning();

      if (!updatedPlan) {
        return res.status(404).json({ message: 'Plan not found' });
      }

      // Broadcast real-time update
      realtimeService.broadcast({
        resource: 'saas-plans',
        action: 'update',
        data: updatedPlan,
        id
      });

      res.json({
        message: 'Plan updated successfully',
        plan: updatedPlan
      });
    } catch (error) {
      console.error('Error updating plan:', error);
      res.status(500).json({ message: 'Failed to update plan' });
    }
  });

  // Public Routes - No authentication required
  app.get('/api/saas/plans', async (req, res) => {
    try {
      const publicPlans = await db
        .select({
          id: plans.id,
          name: plans.name,
          description: plans.description,
          price: plans.price,
          currency: plans.currency,
          billingPeriod: plans.billingPeriod,
          features: plans.features,
          maxUsers: plans.maxUsers,
          maxTransactionsPerMonth: plans.maxTransactionsPerMonth,
          maxStorageGB: plans.maxStorageGB,
          whatsappIntegration: plans.whatsappIntegration,
          customBranding: plans.customBranding,
          apiAccess: plans.apiAccess,
          prioritySupport: plans.prioritySupport
        })
        .from(plans)
        .where(eq(plans.isActive, true))
        .orderBy(plans.price);

      res.json(publicPlans);
    } catch (error) {
      console.error('Error fetching public plans:', error);
      res.status(500).json({ message: 'Failed to fetch plans' });
    }
  });

  // Feature-gated routes examples
  app.post('/api/whatsapp/send', isAuthenticated, checkSubscriptionLimits('whatsapp'), async (req: any, res) => {
    try {
      // WhatsApp send logic here
      res.json({ 
        message: 'WhatsApp message sent successfully',
        plan: req.plan?.name,
        remainingQuota: 'unlimited' 
      });
    } catch (error) {
      console.error('Error sending WhatsApp:', error);
      res.status(500).json({ message: 'Failed to send WhatsApp message' });
    }
  });

  app.post('/api/data/export', isAuthenticated, checkSubscriptionLimits('export'), async (req: any, res) => {
    try {
      // Data export logic here
      res.json({ 
        message: 'Data export initiated successfully',
        plan: req.plan?.name,
        exportUrl: '/downloads/export.xlsx'
      });
    } catch (error) {
      console.error('Error exporting data:', error);
      res.status(500).json({ message: 'Failed to export data' });
    }
  });

  // Auto-expire subscriptions (runs periodically)
  app.post('/api/admin/saas/check-expirations', isAuthenticated, requirePermission('saas_admin'), async (req, res) => {
    try {
      const now = new Date();

      // Find expired subscriptions
      const expiredSubscriptions = await db
        .select()
        .from(subscriptions)
        .where(
          and(
            eq(subscriptions.paymentStatus, 'paid'),
            lt(subscriptions.endDate, now)
          )
        );

      let updated = 0;

      for (const subscription of expiredSubscriptions) {
        // Update client status to expired
        await db
          .update(clients)
          .set({ 
            status: 'expired',
            updatedAt: new Date()
          })
          .where(eq(clients.id, subscription.clientId));

        // Update subscription status
        await db
          .update(subscriptions)
          .set({ 
            paymentStatus: 'failed',
            updatedAt: new Date()
          })
          .where(eq(subscriptions.id, subscription.id));

        updated++;
      }

      res.json({
        message: `Checked and updated ${updated} expired subscriptions`,
        expiredCount: updated
      });
    } catch (error) {
      console.error('Error checking subscription expirations:', error);
      res.status(500).json({ message: 'Failed to check expirations' });
    }
  });

  // Warranty Claims API Endpoints

  // GET /api/warranty-claims - List warranty claims with optional filtering
  app.get('/api/warranty-claims', isAuthenticated, async (req, res) => {
    try {
      const { status } = req.query;
      const claims = await storage.getWarrantyClaims(status as string);
      res.json(claims);
    } catch (error) {
      console.error("Error fetching warranty claims:", error);
      res.status(500).json({ message: "Failed to fetch warranty claims" });
    }
  });

  // GET /api/warranty-claims/:id - Get warranty claim by ID
  app.get('/api/warranty-claims/:id', isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      const claim = await storage.getWarrantyClaimById(id);

      if (!claim) {
        return res.status(404).json({ message: "Warranty claim not found" });
      }

      res.json(claim);
    } catch (error) {
      console.error("Error fetching warranty claim:", error);
      res.status(500).json({ message: "Failed to fetch warranty claim" });
    }
  });

  // POST /api/warranty-claims - Create new warranty claim
  app.post('/api/warranty-claims', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.session.user?.id;
      if (!userId) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      // Validate request data
      const claimData = insertWarrantyClaimSchema.parse({
        ...req.body
      });

      // Validate warranty eligibility
      const eligibilityCheck = await storage.validateWarrantyEligibility(
        claimData.originalTransactionId || undefined,
        claimData.originalServiceTicketId || undefined
      );

      if (!eligibilityCheck.isValid) {
        return res.status(400).json({ 
          message: eligibilityCheck.message,
          isEligible: false
        });
      }

      // Create warranty claim
      const claim = await storage.createWarrantyClaim(claimData);

      // Broadcast warranty claim creation to all clients
      realtimeService.broadcastToTenant(req.clientId, {
        resource: 'warranty-claims',
        action: 'create',
        data: claim,
        id: claim.id
      });

      res.status(201).json({
        claim,
        message: "Warranty claim created successfully"
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({
          message: "Validation error",
          errors: error.errors
        });
      }

      console.error("Error creating warranty claim:", error);
      res.status(500).json({ message: "Failed to create warranty claim" });
    }
  });

  // PUT /api/warranty-claims/:id/process - Process warranty claim (approve/reject)
  app.put('/api/warranty-claims/:id/process', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const { action, adminNotes, returnCondition } = req.body;
      const userId = req.session.user?.id;

      if (!userId) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      // Validate action and map to status
      if (!['approve', 'reject'].includes(action)) {
        return res.status(400).json({ 
          message: "Action must be either 'approve' or 'reject'" 
        });
      }

      // Map frontend action to backend status
      const status = action === 'approve' ? 'approved' : 'rejected';

      // Get warranty claim details
      const existingClaim = await storage.getWarrantyClaimById(id);
      if (!existingClaim) {
        return res.status(404).json({ message: "Warranty claim not found" });
      }

      if (existingClaim.status !== 'pending') {
        return res.status(400).json({ 
          message: "Only pending warranty claims can be processed" 
        });
      }

      // For sales return claims that are approved, require returnCondition
      if (existingClaim.claimType === 'sales_return' && status === 'approved') {
        if (!returnCondition || !['normal_stock', 'damaged_stock'].includes(returnCondition)) {
          return res.status(400).json({
            message: "returnCondition is required for approved sales return claims and must be 'normal_stock' or 'damaged_stock'"
          });
        }
      }

      // Process warranty claim
      const updatedClaim = await storage.processWarrantyClaim(
        id, 
        status, 
        userId, 
        returnCondition
      );

      // Broadcast warranty claim update to all clients
      realtimeService.broadcastToTenant(req.clientId, {
        resource: 'warranty-claims',
        action: 'update',
        data: updatedClaim,
        id: updatedClaim.id
      });

      res.json({
        claim: updatedClaim,
        message: `Warranty claim ${status} successfully`
      });
    } catch (error) {
      console.error("Error processing warranty claim:", error);
      res.status(500).json({ message: "Failed to process warranty claim" });
    }
  });

  // PUT /api/warranty-claims/:id/complete - Mark warranty claim as completed
  app.put('/api/warranty-claims/:id/complete', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const userId = req.session.user?.id;

      if (!userId) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      // Get warranty claim details
      const existingClaim = await storage.getWarrantyClaimById(id);
      if (!existingClaim) {
        return res.status(404).json({ message: "Warranty claim not found" });
      }

      if (existingClaim.status !== 'approved') {
        return res.status(400).json({ 
          message: "Only approved warranty claims can be marked as completed" 
        });
      }

      // Handle sales return inventory and finance integration
      if (existingClaim.claimType === 'sales_return' && existingClaim.originalTransactionId) {
        await storage.processSalesReturnWarranty(
          existingClaim.originalTransactionId,
          existingClaim.returnCondition || 'normal_stock',
          userId
        );
      }

      // Mark warranty claim as processed/completed
      const updatedClaim = await storage.updateWarrantyClaimStatus(
        id,
        'processed',
        userId
      );

      // Broadcast warranty claim completion to all clients
      realtimeService.broadcastToTenant(req.clientId, {
        resource: 'warranty-claims',
        action: 'update',
        data: updatedClaim,
        id: updatedClaim.id
      });

      res.json({
        claim: updatedClaim,
        message: "Warranty claim marked as completed successfully"
      });
    } catch (error) {
      console.error("Error completing warranty claim:", error);
      res.status(500).json({ message: "Failed to complete warranty claim" });
    }
  });

  // GET /api/warranty-claims/validate/:type/:id - Validate warranty eligibility
  app.get('/api/warranty-claims/validate/:type/:id', isAuthenticated, async (req, res) => {
    try {
      const { type, id } = req.params;

      let originalTransactionId: string | undefined;
      let originalServiceTicketId: string | undefined;

      if (type === 'sale') {
        originalTransactionId = id;
      } else if (type === 'service') {
        originalServiceTicketId = id;
      } else {
        return res.status(400).json({ 
          message: "Type must be either 'sale' or 'service'" 
        });
      }

      const eligibilityCheck = await storage.validateWarrantyEligibility(
        originalTransactionId,
        originalServiceTicketId
      );

      res.json({
        isEligible: eligibilityCheck.isValid,
        message: eligibilityCheck.message,
        type,
        id
      });
    } catch (error) {
      console.error("Error validating warranty eligibility:", error);
      res.status(500).json({ 
        message: "Failed to validate warranty eligibility",
        isEligible: false
      });
    }
  });

  const httpServer = createServer(app);

  // Initialize WebSocket server for real-time updates
  realtimeService.initialize(httpServer);

  return httpServer;
}

server/storage.ts.content:
import {
  users,
  roles,
  storeConfig,
  categories,
  products,
  customers,
  suppliers,
  locations,
  productBatches,
  purchaseOrders,
  purchaseOrderItems,
  inventoryAdjustments,
  inventoryAdjustmentItems,
  transactions,
  transactionItems,
  serviceTickets,
  serviceTicketParts,
  stockMovements,
  financialRecords,
  warrantyClaims,
  type User,
  type InsertUser,
  type Role,
  type InsertRole,
  type StoreConfig,
  type InsertStoreConfig,
  type Category,
  type InsertCategory,
  type Product,
  type InsertProduct,
  type Customer,
  type InsertCustomer,
  type Supplier,
  type InsertSupplier,
  type Location,
  type InsertLocation,
  type ProductBatch,
  type InsertProductBatch,
  type PurchaseOrder,
  type InsertPurchaseOrder,
  type PurchaseOrderItem,
  type InsertPurchaseOrderItem,
  type InventoryAdjustment,
  type InsertInventoryAdjustment,
  type InventoryAdjustmentItem,
  type InsertInventoryAdjustmentItem,
  type Transaction,
  type InsertTransaction,
  type TransactionItem,
  type InsertTransactionItem,
  type ServiceTicket,
  type InsertServiceTicket,
  type ServiceTicketPart,
  type InsertServiceTicketPart,
  type StockMovement,
  type InsertStockMovement,
  type FinancialRecord,
  type InsertFinancialRecord,
  type WarrantyClaim,
  type InsertWarrantyClaim,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, asc, and, or, gte, lte, like, ilike, count, sum, sql, isNotNull, gt } from "drizzle-orm";
import {
  getCurrentJakartaTime,
  toJakartaTime,
  formatDateForDatabase,
  parseWithTimezone,
  getStartOfDayJakarta,
  getEndOfDayJakarta,
  createJakartaTimestamp,
  createDatabaseTimestamp
} from "@shared/utils/timezone";
import { FinanceManager } from "./financeManager";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser, clientId?: string): Promise<User>;
  upsertUser(user: Partial<InsertUser> & { id: string }): Promise<User>;

  // User management
  getUsers(): Promise<User[]>;
  getUserCount(clientId?: string): Promise<number>;
  getUserByEmail(email: string): Promise<User | undefined>;
  updateUser(id: string, user: Partial<InsertUser>): Promise<User>;
  deleteUser(id: string): Promise<void>;

  // Role management
  getRoles(): Promise<Role[]>;
  getRoleById(id: string): Promise<Role | undefined>;
  getRoleByName(name: string): Promise<Role | undefined>;
  createRole(role: InsertRole): Promise<Role>;
  updateRole(id: string, role: Partial<InsertRole>): Promise<Role>;
  deleteRole(id: string): Promise<void>;

  // Store configuration
  getStoreConfig(clientId?: string): Promise<StoreConfig | undefined>;
  upsertStoreConfig(config: InsertStoreConfig, clientId?: string): Promise<StoreConfig>;

  // Categories
  getCategories(): Promise<Category[]>;
  getCategoryById(id: string): Promise<Category | undefined>;
  createCategory(category: InsertCategory): Promise<Category>;
  updateCategory(id: string, category: Partial<InsertCategory>): Promise<Category>;
  deleteCategory(id: string): Promise<void>;

  // Products
  getProducts(search?: string): Promise<Product[]>;
  getProductById(id: string): Promise<Product | undefined>;
  getProductBySku(sku: string): Promise<Product | undefined>;
  getProductByBarcode(barcode: string): Promise<Product | undefined>;
  createProduct(product: InsertProduct): Promise<Product>;
  updateProduct(id: string, product: Partial<InsertProduct>): Promise<Product>;
  deleteProduct(id: string): Promise<void>;

  // Locations
  getLocations(): Promise<Location[]>;
  getLocationById(id: string): Promise<Location | undefined>;
  createLocation(location: InsertLocation): Promise<Location>;
  updateLocation(id: string, location: Partial<InsertLocation>): Promise<Location>;
  deleteLocation(id: string): Promise<void>;

  // Product Batches
  getProductBatches(productId?: string): Promise<ProductBatch[]>;
  getProductBatchById(id: string): Promise<ProductBatch | undefined>;
  createProductBatch(batch: InsertProductBatch): Promise<ProductBatch>;
  updateProductBatch(id: string, batch: Partial<InsertProductBatch>): Promise<ProductBatch>;

  // Purchase Orders
  getPurchaseOrders(): Promise<PurchaseOrder[]>;
  getPurchaseOrderById(id: string): Promise<PurchaseOrder | undefined>;
  createPurchaseOrder(po: InsertPurchaseOrder): Promise<PurchaseOrder>;
  updatePurchaseOrder(id: string, po: Partial<InsertPurchaseOrder>): Promise<PurchaseOrder>;
  deletePurchaseOrder(id: string): Promise<void>;
  approvePurchaseOrder(id: string, approvedBy: string): Promise<PurchaseOrder>;

  // Purchase Order Items
  getPurchaseOrderItems(poId: string): Promise<(PurchaseOrderItem & { productName: string; productSku: string })[]>;
  getAllOutstandingItems(): Promise<(PurchaseOrderItem & { productName: string; productSku: string; poNumber: string })[]>;
  createPurchaseOrderItem(item: InsertPurchaseOrderItem): Promise<PurchaseOrderItem>;
  updatePurchaseOrderItem(id: string, item: Partial<InsertPurchaseOrderItem>): Promise<PurchaseOrderItem>;
  deletePurchaseOrderItem(id: string): Promise<void>;
  recalculatePurchaseOrderTotal(poId: string): Promise<void>;
  receivePurchaseOrderItem(itemId: string, receivedQuantity: number, userId: string): Promise<void>;

  // Inventory Adjustments
  getInventoryAdjustments(): Promise<InventoryAdjustment[]>;
  getInventoryAdjustmentById(id: string): Promise<InventoryAdjustment | undefined>;
  createInventoryAdjustment(adjustment: InsertInventoryAdjustment): Promise<InventoryAdjustment>;
  approveInventoryAdjustment(id: string, approvedBy: string): Promise<InventoryAdjustment>;

  // Inventory Adjustment Items
  getInventoryAdjustmentItems(adjustmentId: string): Promise<InventoryAdjustmentItem[]>;
  createInventoryAdjustmentItem(item: InsertInventoryAdjustmentItem): Promise<InventoryAdjustmentItem>;

  // Customers
  getCustomers(): Promise<Customer[]>;
  getCustomerById(id: string): Promise<Customer | undefined>;
  searchCustomers(query: string): Promise<Customer[]>;
  createCustomer(customer: InsertCustomer): Promise<Customer>;
  updateCustomer(id: string, customer: Partial<InsertCustomer>): Promise<Customer>;
  deleteCustomer(id: string): Promise<void>;

  // Suppliers
  getSuppliers(): Promise<Supplier[]>;
  getSupplierById(id: string): Promise<Supplier | undefined>;
  createSupplier(supplier: InsertSupplier): Promise<Supplier>;
  updateSupplier(id: string, supplier: Partial<InsertSupplier>): Promise<Supplier>;
  deleteSupplier(id: string): Promise<void>;

  // Transactions
  getTransactions(limit?: number): Promise<Transaction[]>;
  getTransactionById(id: string): Promise<Transaction | undefined>;
  createTransaction(transaction: InsertTransaction, items: InsertTransactionItem[]): Promise<Transaction>;

  // Service Tickets
  getServiceTickets(): Promise<ServiceTicket[]>;
  getServiceTicketById(id: string): Promise<ServiceTicket | undefined>;
  getActiveServiceTickets(): Promise<ServiceTicket[]>;
  createServiceTicket(ticket: InsertServiceTicket): Promise<ServiceTicket>;
  updateServiceTicket(id: string, ticket: Partial<InsertServiceTicket>, parts?: InsertServiceTicketPart[], userId?: string): Promise<ServiceTicket>;
  deleteServiceTicket(id: string): Promise<void>;
  cancelServiceTicket(id: string, data: {
    cancellationFee: string;
    cancellationReason: string;
    cancellationType: 'before_completed' | 'after_completed' | 'warranty_refund';
    userId: string;
  }): Promise<{ success: boolean; message?: string }>;

  // Stock Movements
  getStockMovements(productId?: string): Promise<StockMovement[]>;
  createStockMovement(movement: InsertStockMovement): Promise<StockMovement>;

  // Financial Records
  getFinancialRecords(startDate?: Date, endDate?: Date): Promise<FinancialRecord[]>;
  createFinancialRecord(record: InsertFinancialRecord): Promise<FinancialRecord>;

  // Reports
  getSalesReport(startDate: Date, endDate: Date): Promise<{ totalSales: string; transactions: any[] }>;
  getServiceReport(startDate: Date, endDate: Date): Promise<{ totalServices: number; tickets: any[] }>;
  getFinancialReport(startDate: Date, endDate: Date): Promise<{ totalIncome: string; totalExpense: string; profit: string; records: any[] }>;
  getInventoryReport(): Promise<{ lowStockCount: number; lowStockProducts: any[]; totalProducts: number }>;

  // Enhanced Accounting Reports
  getBalanceSheet(asOfDate?: Date): Promise<any>;
  getIncomeStatement(startDate?: Date, endDate?: Date): Promise<any>;
  getChartOfAccounts(): Promise<any[]>;
  createJournalEntry(data: any): Promise<{ success: boolean; journalEntry?: any; error?: string }>;

  // Dashboard Statistics
  getDashboardStats(): Promise<{
    todaySales: string;
    todayRevenue: string;
    activeServices: number;
    lowStockCount: number;
    monthlyProfit: string;
    whatsappConnected: boolean;
  }>;

  // Warranty Claims
  getWarrantyClaims(status?: string): Promise<WarrantyClaim[]>;
  getWarrantyClaimById(id: string): Promise<WarrantyClaim | undefined>;
