            Lihat Semua <ArrowRight className="w-4 h-4 ml-1" />
          </Button>
        </div>
      </CardHeader>
      <CardContent className="p-6">
        {isLoading ? (
          <div className="space-y-3">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-16 bg-muted rounded animate-pulse" />
            ))}
          </div>
        ) : !transactions || transactions.length === 0 ? (
          <p className="text-muted-foreground text-center py-8">
            Tidak ada transaksi. Mulai dengan membuat transaksi baru.
          </p>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full text-sm">
              <thead className="text-xs text-muted-foreground uppercase border-b">
                <tr>
                  <th className="text-left py-3">ID</th>
                  <th className="text-left py-3">Pelanggan</th>
                  <th className="text-left py-3">Jenis</th>
                  <th className="text-right py-3">Jumlah</th>
                  <th className="text-left py-3">Status</th>
                </tr>
              </thead>
              <tbody className="text-foreground">
                {transactions.slice(0, 5).map((transaction: any) => (
                  <tr key={transaction.id} className="border-b hover:bg-muted/50 transition-colors">
                    <td className="py-3 font-medium" data-testid={`transaction-id-${transaction.id}`}>
                      {transaction.transactionNumber || transaction.id}
                    </td>
                    <td className="py-3">
                      {transaction.customer?.name || "Pelanggan Walk-in"}
                    </td>
                    <td className="py-3">
                      <Badge variant="outline" className="flex items-center w-fit">
                        {transaction.type === 'sale' ? (
                          <ShoppingCart className="w-3 h-3 mr-1" />
                        ) : (
                          <Wrench className="w-3 h-3 mr-1" />
                        )}
                        {transaction.type === 'sale' ? 'Penjualan' : 'Servis'}
                      </Badge>
                    </td>
                    <td className="py-3 text-right font-medium">
                      Rp {Number(transaction.total).toLocaleString('id-ID')}
                    </td>
                    <td className="py-3">
                      <Badge variant="secondary" className="flex items-center w-fit">
                        <CheckCircle className="w-3 h-3 mr-1" />
                        Selesai
                      </Badge>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/dashboard/service-.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Laptop, Clock, CheckCircle, AlertTriangle } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { formatDateShort } from '@shared/utils/timezone';

export default function ServiceStatus() {
  const { data: serviceTickets, isLoading } = useQuery({
    queryKey: ["/api/service-tickets"],
    queryFn: async () => {
      const response = await fetch('/api/service-tickets?active=true');
      if (!response.ok) throw new Error('Failed to fetch service tickets');
      return response.json();
    },
    retry: false,
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'secondary';
      case 'in_progress':
        return 'default';
      case 'completed':
        return 'secondary';
      case 'delivered':
        return 'secondary';
      default:
        return 'destructive';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending':
        return Clock;
      case 'in_progress':
        return Clock;
      case 'completed':
        return CheckCircle;
      case 'delivered':
        return CheckCircle;
      default:
        return AlertTriangle;
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'pending':
        return 'Menunggu';
      case 'in_progress':
        return 'Dikerjakan';
      case 'completed':
        return 'Selesai';
      case 'delivered':
        return 'Terkirim';
      default:
        return 'Tertunda';
    }
  };

  return (
    <Card className="shadow-sm">
      <CardHeader className="border-b">
        <CardTitle>Status Servis</CardTitle>
      </CardHeader>
      <CardContent className="p-6">
        {isLoading ? (
          <div className="space-y-3">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-16 bg-muted rounded animate-pulse" />
            ))}
          </div>
        ) : !serviceTickets || serviceTickets.length === 0 ? (
          <p className="text-muted-foreground text-center py-8">
            Tidak ada tiket servis aktif.
          </p>
        ) : (
          <div className="space-y-4">
            {serviceTickets.slice(0, 5).map((ticket: any) => {
              const StatusIcon = getStatusIcon(ticket.status);

              return (
                <div 
                  key={ticket.id} 
                  className="flex items-center justify-between p-3 rounded-md border hover:bg-muted/50 transition-colors"
                  data-testid={`service-ticket-${ticket.id}`}
                >
                  <div className="flex items-center space-x-3">
                    <div className="w-10 h-10 bg-secondary/10 rounded-lg flex items-center justify-center">
                      <Laptop className="w-5 h-5 text-secondary" />
                    </div>
                    <div>
                      <p className="text-sm font-medium text-foreground">
                        {ticket.customer?.name || "Unknown Customer"}
                      </p>
                      <p className="text-xs text-muted-foreground">
                        {ticket.deviceBrand} {ticket.deviceModel} - {ticket.problem}
                      </p>
                    </div>
                  </div>
                  <div className="text-right">
                    <Badge 
                      variant={getStatusColor(ticket.status)}
                      className="flex items-center"
                    >
                      <StatusIcon className="w-3 h-3 mr-1" />
                      {getStatusText(ticket.status)}
                    </Badge>
                    <p className="text-xs text-muted-foreground mt-1">
                      {ticket.estimatedCompletion 
                        ? `Est: ${formatDateShort(ticket.estimatedCompletion)}`
                        : "No estimate"
                      }
                    </p>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

