import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { LogIn, Laptop } from "lucide-react";

const loginSchema = z.object({
  username: z.string().min(1, "Username harus diisi"),
  password: z.string().min(1, "Password harus diisi"),
});

type LoginFormData = z.infer<typeof loginSchema>;

export default function Login() {
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);

  // Get store config for app name - WITH BETTER CACHING
  const { data: storeConfig } = useQuery({
    queryKey: ['store-config-login'], // Unique key
    queryFn: async () => {
      const response = await fetch('/api/store-config', { credentials: 'include' });
      if (!response.ok) return { name: 'LaptopPOS' };
      return response.json();
    },
    staleTime: Infinity,
    refetchInterval: false,
    refetchOnWindowFocus: false,
    retry: false,
  });

  const form = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: "",
      password: "",
    },
  });

  const loginMutation = useMutation({
    mutationFn: async (data: LoginFormData) => {
      return apiRequest('POST', '/api/auth/login', data);
    },
    onSuccess: () => {
      toast({
        title: "Login Berhasil", 
        description: "Selamat datang di LaptopPOS!",
      });
      // Reload to trigger authentication state update
      window.location.href = "/";
    },
    onError: (error: any) => {
      toast({
        title: "Login Gagal",
        description: error.message || "Username atau password salah",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: LoginFormData) => {
    setIsLoading(true);
    loginMutation.mutate(data);
    setIsLoading(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800 px-4">
      <Card className="w-full max-w-md shadow-2xl border-0">
        <CardHeader className="space-y-1 text-center pb-8">
          <div className="flex justify-center mb-4">
            <div className="w-16 h-16 bg-blue-600 rounded-full flex items-center justify-center">
              <Laptop className="w-8 h-8 text-white" />
            </div>
          </div>
          <CardTitle className="text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
            {(storeConfig as any)?.name || 'LaptopPOS'}
          </CardTitle>
          <CardDescription className="text-gray-600 dark:text-gray-400">
            Sistem Manajemen Penjualan & Servis Laptop
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          <Form {...form}>
            <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="username"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-gray-700 dark:text-gray-300">
                      Username
                    </FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Masukkan username Anda"
                        {...field}
                        data-testid="input-username"
                        className="h-11"
                        disabled={isLoading}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-gray-700 dark:text-gray-300">
                      Password
                    </FormLabel>
                    <FormControl>
                      <Input
                        type="password"
                        placeholder="Masukkan password Anda"
                        {...field}
                        data-testid="input-password"
                        className="h-11"
                        disabled={isLoading}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <Button
                type="submit"
                className="w-full h-11 bg-blue-600 hover:bg-blue-700 text-white font-medium"
                disabled={isLoading || loginMutation.isPending}
                data-testid="button-login"
              >
                {isLoading || loginMutation.isPending ? (
                  <>
                    <LogIn className="w-4 h-4 mr-2 animate-spin" />
                    Masuk...
                  </>
                ) : (
                  <>
                    <LogIn className="w-4 h-4 mr-2" />
                    Masuk
                  </>
                )}
              </Button>
            </form>
          </Form>

          <div className="text-center pt-4 border-t border-gray-200 dark:border-gray-700">
            <p className="text-xs text-gray-500 dark:text-gray-400">
              Gunakan kredensial akun Anda untuk mengakses sistem
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

server/auth.ts.content:
import bcrypt from 'bcryptjs';
import session from 'express-session';
import type { Express, RequestHandler } from 'express';
import connectPg from 'connect-pg-simple';
import { storage } from './storage';
import type { User, LoginUser } from '@shared/schema';

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: true, // Auto-create table during fresh setup
    ttl: sessionTtl,
    tableName: 'sessions',
  });
  return session({
    secret: process.env.SESSION_SECRET || 'your-secret-key-change-this',
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: 'auto', // Auto-detect HTTPS for deployment compatibility
      sameSite: 'lax', // Better compatibility with modern browsers
      maxAge: sessionTtl,
    },
    name: 'laptoppos.session', // Custom session name
    proxy: true, // Trust proxy for deployment
  });
}

export async function setupAuth(app: Express) {
  app.set('trust proxy', 1);
  app.use(getSession());

  // Create default admin user if it doesn't exist (for local deployment)
  await createDefaultAdminUser();
}

async function createDefaultAdminUser() {
  try {
    const adminUsername = process.env.DEFAULT_ADMIN_USERNAME || 'admin';
    const adminPassword = process.env.DEFAULT_ADMIN_PASSWORD || 'admin123';
    const adminEmail = process.env.DEFAULT_ADMIN_EMAIL || 'admin@laptoppos.com';

    // Check if admin user exists
    const existingUser = await storage.getUserByUsername(adminUsername);

    if (!existingUser) {
      // Create default admin user
      const hashedPassword = await hashPassword(adminPassword);

      await storage.createUser({
        id: 'admin-' + Date.now(),
        username: adminUsername,
        email: adminEmail,
        firstName: 'System',
        lastName: 'Administrator',
        password: hashedPassword,
        role: 'admin',
        isActive: true,
        profileImageUrl: null
      });

      console.log(`âœ… Default admin user created: ${adminUsername}/${adminPassword}`);
    } else {
      // Ensure existing admin has correct password (for deployment consistency)
      const isValidPassword = await verifyPassword(adminPassword, existingUser.password || '');
      if (!isValidPassword) {
        const hashedPassword = await hashPassword(adminPassword);
        await storage.updateUser(existingUser.id, { password: hashedPassword });
        console.log(`âœ… Admin password reset to default: ${adminUsername}/${adminPassword}`);
      }
    }
  } catch (error) {
    console.error('Error creating default admin user:', error);
  }
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  console.log("Auth check:", {
    hasSession: !!req.session,
    hasUser: !!req.session?.user,
    userId: req.session?.user?.id,
    sessionId: req.sessionID,
    path: req.path,
    method: req.method
  });

  if (req.session && req.session.user) {
    return next();
  }

  console.error("Authentication failed:", {
    sessionExists: !!req.session,
    userExists: !!req.session?.user,
    path: req.path
  });
  return res.status(401).json({ message: 'Unauthorized' });
};

export async function hashPassword(password: string): Promise<string> {
  const saltRounds = 10;
  return bcrypt.hash(password, saltRounds);
}

export async function verifyPassword(password: string, hash: string): Promise<boolean> {
  return bcrypt.compare(password, hash);
}

export async function authenticateUser(credentials: LoginUser): Promise<User | null> {
  try {
    const user = await storage.getUserByUsername(credentials.username);
    if (!user || !user.password) {
      return null;
    }

    const isValidPassword = await verifyPassword(credentials.password, user.password);
    if (!isValidPassword) {
      return null;
    }

    // Don't return password in user object
    const { password, ...userWithoutPassword } = user;
    return userWithoutPassword as User;
  } catch (error) {
    console.error('Authentication error:', error);
    return null;
  }
}

// Extend session interface
declare module 'express-session' {
  interface SessionData {
    user: User;
  }
}

server/whatsappService.ts.content:
import { makeWASocket, DisconnectReason, useMultiFileAuthState } from '@whiskeysockets/baileys';
import { Boom } from '@hapi/boom';
import qrcode from 'qrcode-terminal';
import { storage } from './storage';

export class WhatsAppService {
  private socket: any = null;
  private isConnecting = false;
  private qrCode: string | null = null;
  private connectionState: string = 'close';
  private heartbeatInterval: NodeJS.Timeout | null = null;

  async initialize() {
    if (this.isConnecting) {
      console.log('WhatsApp already connecting...');
      return;
    }

    this.isConnecting = true;
    console.log('ðŸ”Œ Initializing WhatsApp connection...');

    try {
      // Use file-based auth state
      const { state, saveCreds } = await useMultiFileAuthState('auth_info_baileys');

      // Create proper logger with required methods
      const logger = {
        level: 'silent',
        error: () => {},
        warn: () => {},
        info: () => {},
        debug: () => {},
        trace: () => {},
        child: () => logger,
      };

      this.socket = makeWASocket({
        auth: state,
        printQRInTerminal: false,
        logger: logger as any,
      });

      // Connection updates
      this.socket.ev.on('connection.update', async (update: any) => {
        const { connection, lastDisconnect, qr } = update;

        if (qr) {
          this.qrCode = qr;
          qrcode.generate(qr, { small: true });
          console.log('QR Code updated');
          await this.updateQRInDatabase();
        }

        if (connection === 'close') {
          this.connectionState = 'close';
          const shouldReconnect = (lastDisconnect?.error as Boom)?.output?.statusCode !== DisconnectReason.loggedOut;
          console.log('Connection closed due to ', lastDisconnect?.error, ', reconnecting ', shouldReconnect);

          if (shouldReconnect) {
            setTimeout(() => this.initialize(), 3000);
          } else {
            console.log('WhatsApp logged out');
            this.qrCode = null;
            await this.clearQRFromDatabase();
          }

          this.isConnecting = false;
          await this.updateConnectionStatus(false);
        } else if (connection === 'open') {
          this.connectionState = 'open';
          this.isConnecting = false;
          this.qrCode = null;
          console.log('âœ… WhatsApp connected successfully');
          await this.updateConnectionStatus(true);
          await this.clearQRFromDatabase();

          // Start connection health monitoring
          this.startConnectionMonitoring();
        }
      });

      // Save credentials when updated
      this.socket.ev.on('creds.update', saveCreds);

    } catch (error) {
      console.error('WhatsApp initialization error:', error);
      this.isConnecting = false;
      await this.updateConnectionStatus(false);
    }
  }


  async sendMessage(phoneNumber: string, message: string): Promise<boolean> {
    // Enhanced connection checking
    console.log(`WhatsApp sendMessage attempt: socket=${!!this.socket}, connectionState=${this.connectionState}, isConnecting=${this.isConnecting}`);

    if (!this.socket || this.connectionState !== 'open') {
      console.log(`WhatsApp not ready - Socket: ${!!this.socket}, State: ${this.connectionState}, Connecting: ${this.isConnecting}`);

      // Auto-reconnect if disconnected but not already connecting
      if (!this.isConnecting && this.connectionState === 'close') {
        console.log('Attempting WhatsApp auto-reconnect...');
        await this.initialize(); // Wait for reconnection attempt

        // Check again after reconnection attempt
        if (this.connectionState === 'open' && this.socket) {
          console.log('WhatsApp reconnected successfully, retrying message send...');
        } else {
          console.log('WhatsApp reconnection failed, message cannot be sent');
          return false;
        }
      } else {
        return false;
      }
    }

    try {
      // Format phone number (add country code if not present)
      let formattedNumber = phoneNumber.replace(/\D/g, '');
      if (!formattedNumber.startsWith('62')) {
        if (formattedNumber.startsWith('0')) {
          formattedNumber = '62' + formattedNumber.substring(1);
        } else {
          formattedNumber = '62' + formattedNumber;
        }
      }

      const jid = formattedNumber + '@s.whatsapp.net';

      console.log(`Sending WhatsApp to ${formattedNumber} (original: ${phoneNumber})`);
      await this.socket.sendMessage(jid, { text: message });
      console.log(`âœ… WhatsApp message sent successfully to ${phoneNumber}`);
      return true;
    } catch (error) {
      console.error('âŒ Error sending WhatsApp message:', error);

      // Check if connection lost during send
      if (this.connectionState !== 'open') {
        console.log('Connection lost during send, marking as disconnected');
        await this.updateConnectionStatus(false);
      }

      return false;
    }
  }

  getQRCode(): string | null {
    return this.qrCode;
  }

  isConnected(): boolean {
    return this.connectionState === 'open';
  }

  getConnectionState(): string {
    return this.connectionState;
  }

  private async updateConnectionStatus(connected: boolean) {
    try {
      const config = await storage.getStoreConfig();
      if (config) {
        await storage.upsertStoreConfig({
          ...config,
          whatsappConnected: connected,
        });
      }
    } catch (error) {
      console.error('Error updating WhatsApp connection status:', error);
    }
  }

  private async updateQRInDatabase() {
    try {
      const config = await storage.getStoreConfig();
      if (config && this.qrCode) {
        await storage.upsertStoreConfig({
          ...config,
          whatsappQR: this.qrCode,
        });
      }
    } catch (error) {
      console.error('Error updating QR code in database:', error);
    }
  }

  private async clearQRFromDatabase() {
    try {
      const config = await storage.getStoreConfig();
      if (config) {
        await storage.upsertStoreConfig({
          ...config,
          whatsappQR: null,
        });
      }
    } catch (error) {
      console.error('Error clearing QR code from database:', error);
    }
  }

  // Service notification templates
  async sendServiceCreatedNotification(customerPhone: string, serviceTicket: any, customer: any, storeConfig: any): Promise<boolean> {
    console.log(`ðŸ“§ Attempting to send service creation notification to ${customerPhone} for ticket ${serviceTicket.ticketNumber}`);
    // Dynamic domain detection for different deployment scenarios
    const getBaseUrl = () => {
      // Check for custom domain environment variable (untuk ngrok, cloudflare, dll)
      if (process.env.PUBLIC_URL) {
        return process.env.PUBLIC_URL;
      }

      // Check for Replit domains
      if (process.env.REPLIT_DOMAINS?.split(',')[0]) {
        return 'https://' + process.env.REPLIT_DOMAINS.split(',')[0];
      }

      // Check for custom app URL (untuk ngrok dll)
      if (process.env.APP_URL) {
        return process.env.APP_URL;
      }

      // Check for ngrok URL pattern in environment
      if (process.env.NGROK_URL) {
        return process.env.NGROK_URL;
      }

      // Production domain - profesionalservis.my.id
      if (process.env.NODE_ENV === 'production' && process.env.DOMAIN_NAME) {
        return `https://${process.env.DOMAIN_NAME}`;
      }

      // Fallback to localhost with current port
      const port = process.env.PORT || '3000';
      return `http://localhost:${port}`;
    };

    const statusUrl = `${getBaseUrl()}/service-status`;

    // Format estimated cost
    const formatCurrency = (amount: string | number) => {
      return new Intl.NumberFormat('id-ID', {
        style: 'currency',
        currency: 'IDR',
        minimumFractionDigits: 0,
      }).format(Number(amount));
    };

    // Format received date
    const receivedDate = new Date(serviceTicket.createdAt).toLocaleDateString('id-ID', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });

    // Status label
    const statusLabels = {
      'pending': 'Menunggu Pemeriksaan',
      'checking': 'Sedang Dicek',
      'in-progress': 'Sedang Dikerjakan',
      'waiting-parts': 'Menunggu Sparepart',
      'waiting-payment': 'Menunggu Pembayaran',
      'completed': 'Selesai',
      'delivered': 'Sudah Diambil',
      'cancelled': 'Dibatalkan'
    };

    // Build spare parts info if available
    let sparepartsInfo = '';
    if (serviceTicket.parts && serviceTicket.parts.length > 0) {
      sparepartsInfo = `\n\nðŸ“¦ **SPAREPART YANG DIPERLUKAN:**`;
      serviceTicket.parts.forEach((part: any, index: number) => {
        sparepartsInfo += `\n${index + 1}. ${part.name}`;
        sparepartsInfo += `\n   â€¢ Jumlah: ${part.quantity} unit`;
        sparepartsInfo += `\n   â€¢ Harga: ${formatCurrency(part.unitPrice)}`;
        if (part.description) {
          sparepartsInfo += `\n   â€¢ Keterangan: ${part.description}`;
        }
      });

      const totalParts = serviceTicket.parts.reduce((sum: number, part: any) => 
        sum + (Number(part.quantity) * Number(part.unitPrice)), 0
      );
      sparepartsInfo += `\n\nðŸ’° **Total Sparepart:** ${formatCurrency(totalParts)}`;
    }

    const message = `ðŸ”§ **KONFIRMASI PENERIMAAN SERVICE**

Halo ${customer.name},

Service laptop Anda telah kami terima dengan detail lengkap sebagai berikut:

ðŸ“‹ **INFORMASI SERVICE:**
ðŸ“ Nomor Service: *${serviceTicket.ticketNumber}*
ðŸ“… Tanggal Diterima: ${receivedDate}
â° Status Saat Ini: *${statusLabels[serviceTicket.status as keyof typeof statusLabels] || 'Menunggu'}*

ðŸ’» **DETAIL PERANGKAT:**
ðŸ·ï¸ Jenis: ${serviceTicket.deviceType}
${serviceTicket.deviceBrand ? `ðŸ­ Merk: ${serviceTicket.deviceBrand}` : ''}
${serviceTicket.deviceModel ? `ðŸ“± Model: ${serviceTicket.deviceModel}` : ''}
${serviceTicket.serialNumber ? `ðŸ”¢ Serial Number: ${serviceTicket.serialNumber}` : ''}

ðŸ” **KELUHAN & MASALAH:**
${serviceTicket.problem}

${serviceTicket.symptoms ? `ðŸ©º **GEJALA YANG DIALAMI:**\n${serviceTicket.symptoms}\n\n` : ''}
${serviceTicket.notes ? `ðŸ“Œ **CATATAN TEKNISI:**\n${serviceTicket.notes}\n\n` : ''}
ðŸ’° **ESTIMASI BIAYA SERVICE:**
${serviceTicket.estimatedCost ? formatCurrency(serviceTicket.estimatedCost) : 'Akan diberitahu setelah pemeriksaan'}
${sparepartsInfo}

ðŸ“ž **INFORMASI KONTAK:**
ðŸ‘¤ Customer: ${customer.name}
ðŸ“± Telepon: ${customer.phone}
${customer.email ? `ðŸ“§ Email: ${customer.email}` : ''}
${customer.address ? `ðŸ  Alamat: ${customer.address}` : ''}

ðŸ” **CEK STATUS SERVICE:**
Anda dapat memantau perkembangan service kapan saja melalui:
${statusUrl}?ticket=${serviceTicket.ticketNumber}
*Klik link di atas untuk langsung melihat status service Anda*

âš ï¸ **PENTING:**
â€¢ Harap simpan nomor service untuk tracking
â€¢ Kami akan menghubungi jika ada update penting
â€¢ Estimasi waktu pengerjaan: ${serviceTicket.estimatedDays ? serviceTicket.estimatedDays + ' hari' : '3-7 hari kerja'}
â€¢ Bawa tanda terima saat pengambilan

Terima kasih telah mempercayakan perangkat Anda kepada kami. Kami akan memberikan pelayanan terbaik untuk memperbaiki laptop Anda.

---
ðŸª **${storeConfig?.name || 'LaptopPOS Service Center'}**
ðŸ“ ${storeConfig?.address || 'Alamat Toko'}
ðŸ“ž ${storeConfig?.phone || 'Telepon Toko'}
${storeConfig?.email ? `ðŸ“§ ${storeConfig.email}` : ''}`;

    try {
      const result = await this.sendMessage(customerPhone, message);
      console.log(`ðŸ“§ Service creation notification ${result ? 'sent successfully' : 'failed'} to ${customerPhone}`);
      return result;
    } catch (error) {
      console.error(`âŒ Service creation notification error for ${customerPhone}:`, error);
      return false;
    }
  }

  async sendServiceStatusNotification(customerPhone: string, serviceTicket: any, customer: any, storeConfig: any): Promise<boolean> {
    console.log(`ðŸ”„ Attempting to send status update notification to ${customerPhone} for ticket ${serviceTicket.ticketNumber}, status: ${serviceTicket.status}`);
    // Format currency
    const formatCurrency = (amount: string | number) => {
      return new Intl.NumberFormat('id-ID', {
        style: 'currency',
        currency: 'IDR',
        minimumFractionDigits: 0,
      }).format(Number(amount));
    };

    // Format update date
    const updateDate = new Date().toLocaleDateString('id-ID', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });

    let statusText = '';
    let emoji = '';
    let nextSteps = '';

    switch (serviceTicket.status) {
      case 'checking':
        statusText = 'SEDANG DICEK';
        emoji = 'ðŸ”';
        nextSteps = 'Tim teknisi kami sedang memeriksa perangkat Anda untuk menentukan kerusakan dan solusi yang tepat.';
        break;
      case 'in-progress':
        statusText = 'SEDANG DIKERJAKAN';
        emoji = 'ðŸ”§';
        nextSteps = 'Perangkat Anda sedang dalam proses perbaikan. Tim teknisi kami bekerja untuk menyelesaikan masalah.';
        break;
      case 'completed':
        statusText = 'SELESAI DIKERJAKAN';
        emoji = 'âœ…';
        nextSteps = 'Perbaikan telah selesai! Perangkat siap diambil. Silakan datang ke toko dengan membawa tanda terima.';
        break;
      case 'cancelled':
        statusText = 'DIBATALKAN';
        emoji = 'âŒ';
        nextSteps = 'Service dibatalkan sesuai permintaan. Jika ada pertanyaan, silakan hubungi kami.';
        break;
      case 'waiting-parts':
        statusText = 'MENUNGGU SPAREPART';
        emoji = 'ðŸ“¦';
        nextSteps = 'Kami sedang memesan sparepart yang diperlukan. Akan ada update setelah sparepart tersedia.';
        break;
      case 'waiting-payment':
        statusText = 'MENUNGGU PEMBAYARAN';
        emoji = 'ðŸ’³';
        nextSteps = 'Perbaikan selesai, silakan lakukan pembayaran untuk mengambil perangkat.';
        break;
      case 'waiting-confirmation':
        statusText = 'MENUNGGU KONFIRMASI';
        emoji = 'â“';
        nextSteps = 'Kami memerlukan konfirmasi dari Anda untuk melanjutkan perbaikan. Silakan hubungi kami.';
        break;
      case 'testing':
        statusText = 'SEDANG TES';
        emoji = 'ðŸ§ª';
        nextSteps = 'Sedang dilakukan pengujian untuk memastikan perbaikan berfungsi dengan baik.';
        break;
      default:
        statusText = 'DIUPDATE';
        emoji = 'ðŸ”„';
        nextSteps = 'Status service Anda telah diperbarui. Silakan cek detail lengkap melalui link di bawah.';
    }

    // Dynamic domain detection for different deployment scenarios
    const getBaseUrl = () => {
      // Check for custom domain environment variable (untuk ngrok, cloudflare, dll)
      if (process.env.PUBLIC_URL) {
        return process.env.PUBLIC_URL;
      }

      // Check for Replit domains
      if (process.env.REPLIT_DOMAINS?.split(',')[0]) {
        return 'https://' + process.env.REPLIT_DOMAINS.split(',')[0];
      }

      // Check for custom app URL (untuk ngrok dll)
      if (process.env.APP_URL) {
        return process.env.APP_URL;
      }

      // Check for ngrok URL pattern in environment
      if (process.env.NGROK_URL) {
        return process.env.NGROK_URL;
      }

      // Production domain - profesionalservis.my.id
      if (process.env.NODE_ENV === 'production' && process.env.DOMAIN_NAME) {
        return `https://${process.env.DOMAIN_NAME}`;
      }

      // Fallback to localhost with current port
      const port = process.env.PORT || '3000';
      return `http://localhost:${port}`;
    };

    const statusUrl = `${getBaseUrl()}/service-status`;

    // Build diagnosis and solution info
    let progressInfo = '';
    if (serviceTicket.diagnosis) {
      progressInfo += `\nðŸ” **HASIL DIAGNOSIS:**\n${serviceTicket.diagnosis}`;
    }
    if (serviceTicket.solution) {
      progressInfo += `\n\nðŸ”§ **SOLUSI PERBAIKAN:**\n${serviceTicket.solution}`;
    }
    if (serviceTicket.actualCost && serviceTicket.actualCost > 0) {
      progressInfo += `\n\nðŸ’° **BIAYA AKTUAL:**\n${formatCurrency(serviceTicket.actualCost)}`;
    }

    // Completion info for completed status
    let completionInfo = '';
    if (serviceTicket.status === 'completed' && serviceTicket.completedAt) {
      const completedDate = new Date(serviceTicket.completedAt).toLocaleDateString('id-ID', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
      completionInfo = `\n\nâœ… **WAKTU SELESAI:** ${completedDate}`;
    }

    const message = `${emoji} **UPDATE STATUS SERVICE**\n\nHalo ${customer.name},\n\nAda update untuk service laptop Anda:\n\nðŸ“‹ **INFORMASI SERVICE:**\nðŸ“ Nomor Service: *${serviceTicket.ticketNumber}*\nðŸ“… Update Terakhir: ${updateDate}\nâ° Status: *${statusText}*\n\nðŸ’» **PERANGKAT:**\n${serviceTicket.deviceType}${serviceTicket.deviceBrand ? ` - ${serviceTicket.deviceBrand}` : ''}${serviceTicket.deviceModel ? ` ${serviceTicket.deviceModel}` : ''}\n\nðŸ” **MASALAH:**\n${serviceTicket.problem}${progressInfo}${completionInfo}\n\nðŸ’¬ **LANGKAH SELANJUTNYA:**\n${nextSteps}\n\nðŸ” **CEK STATUS DETAIL:**\nUntuk informasi lebih lengkap, kunjungi:\n${statusUrl}?ticket=${serviceTicket.ticketNumber}\n\n${serviceTicket.status === 'completed' ? 'âš ï¸ **PENTING:** Harap bawa tanda terima saat pengambilan!' : 'ðŸ“ž **INFO:** Kami akan update jika ada perkembangan baru.'}\n\n---\nðŸª **${storeConfig?.name || 'LaptopPOS Service Center'}**\nðŸ“ž ${storeConfig?.phone || 'Telepon Toko'}`;

    try {
      const result = await this.sendMessage(customerPhone, message);
      console.log(`ðŸ”„ Status update notification ${result ? 'sent successfully' : 'failed'} to ${customerPhone}`);
      return result;
    } catch (error) {
      console.error(`âŒ Status update notification error for ${customerPhone}:`, error);
      return false;
    }
  }

  private startConnectionMonitoring() {
    // Clear existing heartbeat if any
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
    }

    // Check connection every 30 seconds
    this.heartbeatInterval = setInterval(() => {
      if (this.connectionState !== 'open' && !this.isConnecting) {
        console.log('ðŸ©º WhatsApp connection lost, attempting reconnection...');
        this.initialize();
      }
    }, 30000);
  }

  async disconnect() {
    // Clear heartbeat monitoring
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
      this.heartbeatInterval = null;
    }

    if (this.socket) {
      await this.socket.logout();
      this.socket = null;
    }
    await this.updateConnectionStatus(false);
    await this.clearQRFromDatabase();
  }
}

