import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { MessageCircle, QrCode, RefreshCw } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";

interface WhatsAppSettingsProps {
  storeConfig: any;
}

export function WhatsAppSettings({ storeConfig }: WhatsAppSettingsProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [testPhone, setTestPhone] = useState("");

  const whatsappEnabled = storeConfig?.whatsappEnabled || false;

  // Query WhatsApp status
  const { data: whatsappStatus, isLoading: statusLoading } = useQuery({
    queryKey: ['/api/whatsapp/status'],
    enabled: whatsappEnabled,
    refetchInterval: false, // Disable automatic polling to prevent infinite loop
    staleTime: 30000, // Cache for 30 seconds
    refetchOnWindowFocus: false,
  });

  // Enable/disable WhatsApp mutation
  const toggleWhatsAppMutation = useMutation({
    mutationFn: async (enabled: boolean) => {
      const response = await fetch(`/api/whatsapp/${enabled ? 'enable' : 'disable'}`, {
        method: 'POST',
        credentials: 'include',
      });
      if (!response.ok) {
        throw new Error('Failed to toggle WhatsApp');
      }
      return response.json();
    },
    onSuccess: () => {
      // Invalidate WhatsApp status and dashboard stats for real-time sync
      queryClient.invalidateQueries({ queryKey: ['/api/whatsapp/status'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
      toast({
        title: "Berhasil",
        description: "Pengaturan WhatsApp berhasil diubah",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error", 
        description: error?.message || "Gagal mengubah pengaturan WhatsApp",
        variant: "destructive",
      });
    },
  });

  // Connect WhatsApp mutation
  const connectWhatsAppMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/whatsapp/connect', {
        method: 'POST',
        credentials: 'include',
      });
      if (!response.ok) {
        throw new Error('Failed to connect WhatsApp');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/whatsapp/status'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
      toast({
        title: "Berhasil",
        description: "Proses koneksi WhatsApp dimulai. Scan QR code untuk menghubungkan.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Gagal menghubungkan WhatsApp",
        variant: "destructive",
      });
    },
  });

  // Disconnect WhatsApp mutation
  const disconnectWhatsAppMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/whatsapp/disconnect', {
        method: 'POST',
        credentials: 'include',
      });
      if (!response.ok) {
        throw new Error('Failed to disconnect WhatsApp');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/whatsapp/status'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
      toast({
        title: "Berhasil",
        description: "WhatsApp berhasil diputuskan",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Gagal memutuskan WhatsApp",
        variant: "destructive",
      });
    },
  });

  // Test message mutation
  const testMessageMutation = useMutation({
    mutationFn: async (phone: string) => {
      const response = await fetch('/api/whatsapp/test-message', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ phoneNumber: phone }),
      });
      if (!response.ok) {
        throw new Error('Failed to send test message');
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Berhasil",
        description: "Pesan test berhasil dikirim",
      });
      setTestPhone("");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Gagal mengirim pesan test",
        variant: "destructive",
      });
    },
  });

  const handleTestMessage = () => {
    if (!testPhone.trim()) {
      toast({
        title: "Error",
        description: "Masukkan nomor telepon terlebih dahulu",
        variant: "destructive",
      });
      return;
    }
    testMessageMutation.mutate(testPhone);
  };

  const whatsappConnected = (whatsappStatus as any)?.connected || false;
  const connectionState = (whatsappStatus as any)?.state || 'close';
  const qrCode = (whatsappStatus as any)?.qrCode;

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <MessageCircle className="w-5 h-5 mr-2" />
          Integrasi WhatsApp
        </CardTitle>
        <CardDescription>
          Hubungkan WhatsApp untuk mengirim notifikasi otomatis ke pelanggan
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Enable/Disable WhatsApp */}
        <div className="flex items-center justify-between p-4 border rounded-lg">
          <div>
            <h4 className="font-medium">Status WhatsApp</h4>
            <p className="text-sm text-muted-foreground">
              {whatsappEnabled ? "WhatsApp integration aktif" : "WhatsApp integration nonaktif"}
            </p>
          </div>
          <Button
            variant={whatsappEnabled ? "destructive" : "default"}
            onClick={() => toggleWhatsAppMutation.mutate(!whatsappEnabled)}
            disabled={toggleWhatsAppMutation.isPending}
            data-testid={whatsappEnabled ? "button-disable-whatsapp" : "button-enable-whatsapp"}
          >
            {toggleWhatsAppMutation.isPending ? "Loading..." : whatsappEnabled ? "Nonaktifkan" : "Aktifkan"}
          </Button>
        </div>

        {/* Connection Status & Actions */}
        <div className="p-4 border rounded-lg">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h4 className="font-medium">Status Koneksi</h4>
              <p className="text-sm text-muted-foreground">
                {whatsappConnected ? (
                  <span className="text-green-600">âœ… Terhubung</span>
                ) : (
                  <span className="text-red-600">âŒ Tidak terhubung</span>
                )}
              </p>
              <p className="text-xs text-muted-foreground mt-1">
                State: {connectionState}
                {statusLoading && <RefreshCw className="w-3 h-3 ml-1 inline animate-spin" />}
              </p>
            </div>

            <div className="space-x-2">
              {!whatsappConnected ? (
                <Button
                  onClick={() => connectWhatsAppMutation.mutate()}
                  disabled={connectWhatsAppMutation.isPending}
                  data-testid="button-connect-whatsapp"
                >
                  {connectWhatsAppMutation.isPending ? (
                    <>
                      <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                      Connecting...
                    </>
                  ) : (
                    <>
                      <QrCode className="w-4 h-4 mr-2" />
                      Connect
                    </>
                  )}
                </Button>
              ) : (
                <Button
                  variant="destructive"
                  onClick={() => disconnectWhatsAppMutation.mutate()}
                  disabled={disconnectWhatsAppMutation.isPending}
                  data-testid="button-disconnect-whatsapp"
                >
                  {disconnectWhatsAppMutation.isPending ? "Disconnecting..." : "Disconnect"}
                </Button>
              )}
            </div>
          </div>

          {/* QR Code Display */}
          {qrCode && !whatsappConnected && (
            <div className="mt-4 p-4 border rounded-lg bg-gray-50 dark:bg-gray-800">
              <h5 className="font-medium mb-2 flex items-center">
                <QrCode className="w-4 h-4 mr-2" />
                Scan QR Code
              </h5>
              <p className="text-sm text-muted-foreground mb-4">
                Buka WhatsApp di ponsel â†’ Settings â†’ Linked Devices â†’ Link a Device â†’ Scan QR code di bawah
              </p>
              <div className="flex justify-center">
                <img 
                  src={qrCode} 
                  alt="WhatsApp QR Code" 
                  className="w-64 h-64 border rounded-lg"
                  data-testid="img-qr-code"
                />
              </div>
              <p className="text-xs text-center text-muted-foreground mt-2">
                QR code akan diperbarui secara otomatis setiap beberapa detik
              </p>
            </div>
          )}
        </div>

        {/* Test Message - Only show when connected */}
        {whatsappConnected && (
          <div className="p-4 border rounded-lg">
            <h4 className="font-medium mb-4">Test Pesan</h4>
            <div className="space-y-3">
              <div>
                <Label htmlFor="testPhone">Nomor Telepon (dengan kode negara)</Label>
                <Input
                  id="testPhone"
                  placeholder="contoh: 628123456789"
                  value={testPhone}
                  onChange={(e) => setTestPhone(e.target.value)}
                  data-testid="input-test-phone"
                />
                <p className="text-xs text-muted-foreground mt-1">
                  Format: 628xxxxxxxxx (tanpa tanda + atau spasi)
                </p>
              </div>
              <Button
                onClick={handleTestMessage}
                disabled={testMessageMutation.isPending || !testPhone.trim()}
                data-testid="button-send-test"
              >
                {testMessageMutation.isPending ? "Mengirim..." : "Kirim Test Pesan"}
              </Button>
            </div>
            <p className="text-sm text-muted-foreground mt-2">
              Kirim pesan test untuk memastikan koneksi WhatsApp berfungsi dengan baik
            </p>
          </div>
        )}

        {/* Feature Information */}
        <div className="space-y-4">
          <div className="border-t pt-4">
            <h4 className="font-medium mb-3">Fitur Notifikasi Otomatis</h4>
            <div className="space-y-2">
              <div className="flex items-center text-sm">
                <div className="w-2 h-2 bg-green-500 rounded-full mr-3"></div>
                <span>Service baru diterima</span>
              </div>
              <div className="flex items-center text-sm">
                <div className="w-2 h-2 bg-green-500 rounded-full mr-3"></div>
                <span>Status service berubah (sedang dikerjakan, selesai, dll)</span>
              </div>
              <div className="flex items-center text-sm">
                <div className="w-2 h-2 bg-green-500 rounded-full mr-3"></div>
                <span>Service siap diambil</span>
              </div>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ServiceReceipt.tsx.content:
import { useState } from 'react';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { formatDateWithTime, formatDateShort } from '@shared/utils/timezone';
import { Printer, Download, FileText } from "lucide-react";
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

interface ServiceReceiptProps {
  serviceData: {
    id: string;
    serviceNumber: string;
    device: string;
    problem: string;
    diagnosis?: string;
    status: string;
    totalCost: string;
    estimatedCompletion?: string;
    completedAt?: string;
    createdAt: string;
    // Warranty fields
    warrantyDuration?: number;
    warrantyStartDate?: string;
    warrantyEndDate?: string;
    customer?: {
      name: string;
      phone?: string;
      email?: string;
    };
    parts?: Array<{
      product: {
        name: string;
      };
      quantity: number;
      unitPrice: string;
      totalPrice: string;
    }>;
  };
  storeConfig?: {
    name: string;
    address?: string;
    phone?: string;
    email?: string;
  };
}

const statusConfig = {
  pending: 'Menunggu',
  'in-progress': 'Dikerjakan',
  'waiting-parts': 'Menunggu Sparepart',
  'waiting-payment': 'Menunggu Pembayaran',
  completed: 'Selesai',
  cancelled: 'Dibatalkan',
};

const paperSizes = {
  '58': { width: 58, name: '58mm (Mini)' },
  '80': { width: 80, name: '80mm (Standard)' },
  '100': { width: 100, name: '100mm (Large)' }
};

export default function ServiceReceipt({ serviceData, storeConfig }: ServiceReceiptProps) {
  const [isGenerating, setIsGenerating] = useState(false);
  const [paperSize, setPaperSize] = useState<'58' | '80' | '100'>('80');

  const formatCurrency = (amount: string | number) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0,
    }).format(Number(amount));
  };

  const generatePDF = async () => {
    setIsGenerating(true);
    try {
      const element = document.getElementById('service-receipt-content');
      if (!element) return;

      const canvas = await html2canvas(element, {
        scale: 2,
        useCORS: true,
        allowTaint: true,
      });

      const imgData = canvas.toDataURL('image/png');
      const thermalWidth = paperSizes[paperSize].width;
      const thermalHeight = (canvas.height / canvas.width) * thermalWidth;

      const pdf = new jsPDF('p', 'mm', [thermalWidth, thermalHeight]);
      pdf.addImage(imgData, 'PNG', 0, 0, thermalWidth, thermalHeight);
      pdf.save(`Nota-Service-${serviceData.serviceNumber}-${thermalWidth}mm.pdf`);
    } catch (error) {
      console.error('Error generating PDF:', error);
    } finally {
      setIsGenerating(false);
    }
  };

  const handlePrint = () => {
    try {
      // Ukuran kertas sesuai thermal yang dipilih
      const thermalWidth = paperSizes[paperSize].width;
      const fontSize = paperSize === '58' ? '7px' : paperSize === '80' ? '8px' : '10px';

      const printStyle = `
        <style id="thermal-print-style">
          @media print {
            * { 
              visibility: hidden; 
              margin: 0 !important; 
              padding: 0 !important;
              box-sizing: border-box;
            }
            #service-receipt-content, 
            #service-receipt-content * { 
              visibility: visible; 
            }
            #service-receipt-content {
              position: absolute;
              left: 0;
              top: 0;
              width: ${thermalWidth}mm;
              max-width: ${thermalWidth}mm;
              font-family: 'Courier New', monospace;
              font-size: ${fontSize};
              line-height: 1.0;
              color: #000;
              background: #fff;
              page-break-inside: avoid;
              page-break-after: avoid;
              page-break-before: avoid;
              height: auto;
              max-height: 250mm;
            }
            .no-print { 
              display: none !important; 
            }
            @page {
              size: ${thermalWidth}mm 300mm;
              margin: 1mm;
            }
            .text-center { text-align: center; }
            .font-bold { font-weight: bold; }
            .flex { display: flex; }
            .flex-1 { flex: 1; }
            .justify-between { justify-content: space-between; }
            .items-center { align-items: center; }
            .border-t { 
              border-top: 1px dashed #333; 
              margin: 1px 0 !important; 
            }
            .border-solid { border-style: solid; }
            .border-gray-800 { border-color: #333; }
            .text-gray-600 { color: #666; }
            .space-y-1 > * + * { margin-top: 1px !important; }
            .py-2 { padding: 1px 0 !important; }
            .my-2 { margin: 1px 0 !important; }
            .mb-2 { margin-bottom: 1px !important; }
            .mt-6 { margin-top: 2px !important; }
            h3 { font-size: ${fontSize}; margin: 1px 0 !important; }
            div { margin: 0 !important; padding: 0 !important; }
            p { margin: 0 !important; padding: 0 !important; }
          }
          @media screen {
            #thermal-print-style { display: none; }
          }
        </style>
      `;

      // Hapus style print lama jika ada
      const oldStyle = document.getElementById('thermal-print-style');
      if (oldStyle) oldStyle.remove();

      // Tambahkan CSS print baru
      document.head.insertAdjacentHTML('beforeend', printStyle);

      // Print
      window.print();

      // Restore setelah delay
      setTimeout(() => {
        const printStyleElement = document.getElementById('thermal-print-style');
        if (printStyleElement) {
          printStyleElement.remove();
        }
      }, 1000);

    } catch (error) {
      console.error('Print error:', error);
      alert('Terjadi kesalahan saat mencetak. Silakan coba lagi.');
    }
  };

  const getReceiptWidth = () => {
    switch (paperSize) {
      case '58': return 'max-w-[58mm]';
      case '80': return 'max-w-[80mm]';
      case '100': return 'max-w-[100mm]';
      default: return 'max-w-[80mm]';
    }
  };

  const getTextSize = () => {
    switch (paperSize) {
      case '58': return 'text-xs';
      case '80': return 'text-sm';
      case '100': return 'text-base';
      default: return 'text-sm';
    }
  };

  return (
    <div className="space-y-4">
      {/* Paper Size Selector */}
      <div className="flex items-center gap-4 p-4 bg-gray-50 rounded-lg no-print">
        <Label htmlFor="paper-size" className="font-medium">Ukuran Kertas:</Label>
        <Select value={paperSize} onValueChange={(value: '58' | '80' | '100') => setPaperSize(value)}>
          <SelectTrigger className="w-48" id="paper-size">
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="58">{paperSizes['58'].name}</SelectItem>
            <SelectItem value="80">{paperSizes['80'].name}</SelectItem>
            <SelectItem value="100">{paperSizes['100'].name}</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <div className="flex gap-2 mb-4 no-print">
        <Button onClick={handlePrint} variant="outline" data-testid="button-print">
          <Printer className="h-4 w-4 mr-2" />
          Print
        </Button>
        <Button 
          onClick={generatePDF} 
          variant="outline"
          disabled={isGenerating}
          data-testid="button-download-pdf"
        >
          <Download className="h-4 w-4 mr-2" />
          {isGenerating ? 'Generating...' : 'Download PDF'}
        </Button>
      </div>

      {/* Receipt Preview */}
      <div className="flex justify-center">
        <Card className={`${getReceiptWidth()} mx-auto`}>
          <CardContent className="p-4">
            <div id="service-receipt-content" className={`space-y-2 ${getTextSize()}`} style={{ fontFamily: 'Courier New, monospace' }}>
              {/* Header */}
              <div className="text-center space-y-1">
                <h2 className={`${paperSize === '58' ? 'text-sm' : paperSize === '80' ? 'text-base' : 'text-lg'} font-bold`} data-testid="text-store-name">
                  {storeConfig?.name || 'LaptopPOS Service Center'}
                </h2>
                {storeConfig?.address && (
                  <p className={`${getTextSize()} text-gray-600`} data-testid="text-store-address">{storeConfig.address}</p>
                )}
                <div className={`flex flex-col ${getTextSize()} text-gray-600`}>
                  {storeConfig?.phone && <span data-testid="text-store-phone">Tel: {storeConfig.phone}</span>}
                  {storeConfig?.email && <span data-testid="text-store-email">{storeConfig.email}</span>}
                </div>
              </div>

              <div className="text-center py-2">
                <h3 className={`${paperSize === '58' ? 'text-sm' : paperSize === '80' ? 'text-base' : 'text-lg'} font-bold`}>NOTA SERVICE</h3>
                <p className={`${getTextSize()} font-semibold`} data-testid="text-service-number">#{serviceData.serviceNumber}</p>
              </div>

              <div className="border-t border-dashed border-gray-400 my-2"></div>

              {/* Service Info - Thermal Layout */}
              <div className={`space-y-1 ${getTextSize()}`}>
                <div className="flex justify-between">
                  <span className="font-bold">Tanggal:</span>
                  <span data-testid="text-service-date">{formatDateWithTime(serviceData.createdAt)}</span>
                </div>
                <div className="flex justify-between">
                  <span className="font-bold">Customer:</span>
                  <span data-testid="text-customer-name">{serviceData.customer?.name || '-'}</span>
                </div>
                {serviceData.customer?.phone && (
                  <div className="flex justify-between">
                    <span className="font-bold">Telepon:</span>
                    <span data-testid="text-customer-phone">{serviceData.customer.phone}</span>
                  </div>
                )}
                <div className="flex justify-between">
                  <span className="font-bold">Status:</span>
                  <span data-testid="text-service-status">{statusConfig[serviceData.status as keyof typeof statusConfig]}</span>
                </div>
              </div>

              <div className="border-t border-dashed border-gray-400 my-2"></div>

              {/* Device & Problem */}
              <div className={`space-y-1 ${getTextSize()}`}>
                <div>
                  <span className="font-bold">Perangkat:</span> <span data-testid="text-device">{serviceData.device}</span>
                </div>
                <div>
                  <span className="font-bold">Keluhan:</span> <span data-testid="text-problem">{serviceData.problem}</span>
                </div>
                {serviceData.diagnosis && (
                  <div>
                    <span className="font-bold">Diagnosis:</span> <span data-testid="text-diagnosis">{serviceData.diagnosis}</span>
                  </div>
                )}
              </div>

              {/* Parts Used */}
              {serviceData.parts && serviceData.parts.length > 0 && (
                <>
                  <div className="border-t border-dashed border-gray-400 my-2"></div>
                  <div>
                    <div className={`font-bold mb-2 ${getTextSize()}`}>Sparepart:</div>
                    {serviceData.parts.map((part, index) => (
                      <div key={index} className={`flex justify-between ${getTextSize()}`}>
                        <div className="flex-1">
                          <div data-testid={`text-part-name-${index}`}>{part.product.name}</div>
                          <div className="text-gray-600">{part.quantity} x {formatCurrency(part.unitPrice)}</div>
                        </div>
                        <div data-testid={`text-part-total-${index}`}>{formatCurrency(part.totalPrice)}</div>
                      </div>
                    ))}
                  </div>
                </>
              )}

              <div className="border-t border-solid border-gray-800 my-2"></div>

              {/* Total */}
              <div className={`flex justify-between items-center font-bold ${getTextSize()}`}>
                <span>Total Service:</span>
                <span data-testid="text-total-cost">{formatCurrency(serviceData.totalCost)}</span>
              </div>

              {/* Warranty Information */}
              {(serviceData as any).warrantyDuration && (
                <>
                  <div className="border-t border-dashed border-gray-400 my-2"></div>
                  <div className={`space-y-1 ${getTextSize()}`}>
                    <div className="text-center font-bold">
                      === INFORMASI GARANSI ===
                    </div>
                    <div className="flex justify-between">
                      <span className="font-bold">Durasi Garansi:</span>
                      <span data-testid="warranty-duration">
                        {(serviceData as any).warrantyDuration === 999999 ? 'Unlimited' : `${(serviceData as any).warrantyDuration} hari`}
                      </span>
                    </div>
                    {(serviceData as any).warrantyStartDate && (
                      <div className="flex justify-between">
                        <span className="font-bold">Mulai Garansi:</span>
                        <span data-testid="warranty-start">
                          {formatDateShort((serviceData as any).warrantyStartDate)}
                        </span>
                      </div>
                    )}
                    {(serviceData as any).warrantyEndDate && (serviceData as any).warrantyDuration !== 999999 && (
                      <div className="flex justify-between">
                        <span className="font-bold">Berakhir:</span>
                        <span data-testid="warranty-end">
                          {formatDateShort((serviceData as any).warrantyEndDate)}
                        </span>
                      </div>
                    )}
                  </div>
                </>
              )}

              <div className="border-t border-dashed border-gray-400 my-2"></div>

              {/* Footer */}
              <div className={`text-center ${getTextSize()} text-gray-600 space-y-1 mt-6`}>
                <div>Terima kasih atas kepercayaan Anda!</div>
                {!(serviceData as any).warrantyDuration && (
                  <div>Garansi service 30 hari</div>
                )}
                <div data-testid="text-print-date">Cetak: {formatDateWithTime(new Date())}</div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/ServiceStatus.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Search, Clock, CheckCircle, AlertCircle, Package, Calendar, Receipt, Settings, TestTube, FileText } from "lucide-react";
import { formatDateLong } from '@shared/utils/timezone';
import ServiceStatusTracker from "@/components/ServiceStatusTracker";

const statusConfig = {
  pending: { label: 'Belum Cek', color: 'bg-yellow-500', icon: Clock },
  checking: { label: 'Sedang Cek', color: 'bg-sky-500', icon: AlertCircle },
  'in-progress': { label: 'Sedang Dikerjakan', color: 'bg-blue-500', icon: Settings },
  'waiting-technician': { label: 'Ditunggu MITRA Teknik', color: 'bg-gray-500', icon: AlertCircle },
  testing: { label: 'Sedang Tes', color: 'bg-indigo-500', icon: TestTube },
  'waiting-confirmation': { label: 'Menunggu Konfirmasi', color: 'bg-red-500', icon: FileText },
  'waiting-parts': { label: 'Menunggu Sparepart', color: 'bg-orange-500', icon: Package },
  'waiting-payment': { label: 'Menunggu Pembayaran', color: 'bg-purple-500', icon: Receipt },
  completed: { label: 'Selesai', color: 'bg-green-500', icon: CheckCircle },
  delivered: { label: 'Sudah Diambil', color: 'bg-emerald-500', icon: CheckCircle },
  cancelled: { label: 'Dibatalkan', color: 'bg-red-500', icon: AlertCircle },
};

export default function ServiceStatus() {
  const [serviceNumber, setServiceNumber] = useState("");
  const [searchClicked, setSearchClicked] = useState(false);
  const [showStatusTracker, setShowStatusTracker] = useState(false);
  const [location] = useLocation();

  // Auto-fill service number from URL parameter
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const ticketParam = urlParams.get('ticket');
    if (ticketParam) {
      setServiceNumber(ticketParam);
      setSearchClicked(true); // Automatically search when ticket is provided
    }
  }, [location]);

  const { data: serviceData, isLoading, error } = useQuery({
    queryKey: ['/api/public/service-status', serviceNumber],
    enabled: searchClicked && serviceNumber.length > 0,
    retry: false,
  }) as {
    data: {
      ticketNumber: string;
      customerName: string;
      deviceType: string;
      deviceBrand: string;
      deviceModel: string;
      problem: string;
      diagnosis?: string;
      status: string;
      estimatedCost?: string;
      estimatedCompletion?: string;
      completedAt?: string;
      createdAt: string;
      parts?: Array<{
        name: string;
        quantity: number;
        unitPrice: string;
      }>;
    } | undefined;
    isLoading: boolean;
    error: any;
  };

  const handleSearch = () => {
    if (serviceNumber.trim()) {
      setSearchClicked(true);
    }
  };

  const formatCurrency = (amount: string | number) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0,
    }).format(Number(amount));
  };

  const status = serviceData?.status ? statusConfig[serviceData.status as keyof typeof statusConfig] : null;
  const StatusIcon = status?.icon || Clock;

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container max-w-2xl mx-auto px-4">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Cek Status Service</h1>
          <p className="text-gray-600">Masukkan nomor service untuk melihat status perbaikan perangkat Anda</p>
        </div>

        <Card className="mb-6">
          <CardContent className="p-6">
            <div className="flex gap-2">
              <Input
                placeholder="Masukkan nomor service (contoh: SRV-20240830-001)"
                value={serviceNumber}
                onChange={(e) => setServiceNumber(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                className="flex-1"
                data-testid="input-service-number"
              />
              <Button onClick={handleSearch} disabled={!serviceNumber.trim()} data-testid="button-search">
                <Search className="h-4 w-4 mr-2" />
                Cari
              </Button>
            </div>
          </CardContent>
        </Card>

        {isLoading && (
          <Card>
            <CardContent className="p-6 text-center">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
              <p className="mt-2 text-gray-600">Mencari data service...</p>
            </CardContent>
          </Card>
        )}

        {error && (
          <Card>
            <CardContent className="p-6 text-center">
              <AlertCircle className="h-12 w-12 text-red-500 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-gray-900 mb-2">Service Tidak Ditemukan</h3>
              <p className="text-gray-600">
                Nomor service yang Anda masukkan tidak ditemukan. Pastikan nomor service benar.
              </p>
            </CardContent>
          </Card>
        )}

        {serviceData && (
          <div className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Receipt className="h-5 w-5" />
                  Detail Service
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Nomor Service</label>
                    <p className="font-semibold" data-testid="text-service-number">{serviceData.ticketNumber}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Status</label>
                    <div className="flex items-center gap-2">
                      <Badge className={`${status?.color} text-white`}>
                        <StatusIcon className="h-3 w-3 mr-1" />
                        {status?.label}
                      </Badge>
                    </div>
                  </div>
                </div>

                <Separator />

                <div>
                  <label className="text-sm font-medium text-gray-500">Nama Customer</label>
                  <p className="font-semibold" data-testid="text-customer-name">{serviceData.customerName}</p>
                </div>

                <div>
                  <label className="text-sm font-medium text-gray-500">Perangkat</label>
                  <p className="font-semibold" data-testid="text-device">{serviceData.deviceType} {serviceData.deviceBrand} {serviceData.deviceModel}</p>
                </div>

                <div>
                  <label className="text-sm font-medium text-gray-500">Keluhan</label>
                  <p className="text-gray-900" data-testid="text-problem">{serviceData.problem}</p>
                </div>

                {serviceData.diagnosis && (
                  <div>
                    <label className="text-sm font-medium text-gray-500">Diagnosis</label>
                    <p className="text-gray-900" data-testid="text-diagnosis">{serviceData.diagnosis}</p>
                  </div>
                )}

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Tanggal Masuk</label>
                    <div className="flex items-center gap-2">
                      <Calendar className="h-4 w-4 text-gray-400" />
                      <span data-testid="text-created-date">
                        {formatDateLong(serviceData.createdAt)}
                      </span>
                    </div>
                  </div>
                  {serviceData.estimatedCompletion && (
                    <div>
                      <label className="text-sm font-medium text-gray-500">Estimasi Selesai</label>
                      <div className="flex items-center gap-2">
                        <Clock className="h-4 w-4 text-gray-400" />
                        <span data-testid="text-estimated-completion">
                          {formatDateLong(serviceData.estimatedCompletion)}
                        </span>
                      </div>
                    </div>
                  )}
                </div>

                {serviceData.completedAt && (
                  <div>
                    <label className="text-sm font-medium text-gray-500">Tanggal Selesai</label>
                    <div className="flex items-center gap-2">
                      <CheckCircle className="h-4 w-4 text-green-500" />
                      <span data-testid="text-completed-date">
                        {formatDateLong(serviceData.completedAt)}
                      </span>
                    </div>
                  </div>
                )}
              </CardContent>
            </Card>

            {serviceData.parts && serviceData.parts.length > 0 && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Package className="h-5 w-5" />
                    Sparepart yang Digunakan
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    {serviceData.parts.map((part: any, index: number) => (
                      <div key={index} className="flex justify-between items-center py-2 border-b border-gray-100 last:border-0">
                        <div>
                          <p className="font-medium" data-testid={`text-part-name-${index}`}>{part.name}</p>
                          <p className="text-sm text-gray-500">Qty: {part.quantity}</p>
                        </div>
                        <p className="font-semibold" data-testid={`text-part-price-${index}`}>
                          {formatCurrency(part.unitPrice)}
                        </p>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}

            <Card>
              <CardContent className="p-6">
                <div className="flex justify-between items-center">
                  <span className="text-lg font-semibold">Estimasi Biaya Service</span>
                  <span className="text-2xl font-bold text-blue-600" data-testid="text-total-cost">
                    {serviceData.estimatedCost ? formatCurrency(serviceData.estimatedCost) : 'Belum ditentukan'}
                  </span>
                </div>
              </CardContent>
            </Card>

            {/* Action Button */}
            <Card>
              <CardContent className="p-6 text-center">
                <Button 
                  onClick={() => setShowStatusTracker(true)}
                  className="flex items-center gap-2 bg-blue-500 hover:bg-blue-600 mx-auto"
                >
                  <Settings className="h-4 w-4" />
                  Detail Status Proses Service
                </Button>
                <p className="text-sm text-gray-500 mt-2">
                  Lihat tahapan detail proses perbaikan perangkat Anda
                </p>
              </CardContent>
            </Card>
          </div>
        )}

        {/* Service Status Tracker Dialog */}
        {serviceData && (
          <ServiceStatusTracker
            isOpen={showStatusTracker}
            onClose={() => setShowStatusTracker(false)}
            serviceNumber={serviceData.ticketNumber}
            currentStatus={serviceData.status}
          />
        )}
      </div>
    </div>
  );
}

