              {/* Parts Used */}
              {serviceTicket.parts && serviceTicket.parts.length > 0 && (
                <div className="mb-4">
                  <h3 className="font-semibold text-sm mb-3 border-b pb-1">SPAREPART YANG DIGUNAKAN</h3>
                  <div className="bg-yellow-50 p-4 rounded">
