import { CheckCircle, Clock, AlertCircle, Package, Settings, TestTube, FileText, X } from 'lucide-react';

interface ServiceStep {
  id: string;
  label: string;
  status: 'completed' | 'current' | 'pending' | 'waiting';
  icon: React.ComponentType<any>;
  color: string;
  bgColor: string;
}

interface ServiceStatusTrackerProps {
  isOpen: boolean;
  onClose: () => void;
  serviceNumber: string;
  currentStatus: string;
}

const serviceSteps: ServiceStep[] = [
  {
    id: 'received',
    label: 'Belum Cek',
    status: 'pending',
    icon: Clock,
    color: 'text-blue-700',
    bgColor: 'bg-blue-100'
  },
  {
    id: 'checking',
    label: 'Sedang Cek',
    status: 'pending',
    icon: AlertCircle,
    color: 'text-sky-700',
    bgColor: 'bg-sky-100'
  },
  {
    id: 'in-progress',
    label: 'Sedang Dikerjakan',
    status: 'pending',
    icon: Settings,
    color: 'text-green-700',
    bgColor: 'bg-green-100'
  },
  {
    id: 'waiting-technician',
    label: 'Ditunggu MITRA Teknik',
    status: 'pending',
    icon: AlertCircle,
    color: 'text-gray-700',
    bgColor: 'bg-gray-100'
  },
  {
    id: 'testing',
    label: 'Sedang Tes',
    status: 'pending',
    icon: TestTube,
    color: 'text-gray-700',
    bgColor: 'bg-gray-100'
  },
  {
    id: 'waiting-confirmation',
    label: 'Menunggu Konfirmasi',
    status: 'pending',
    icon: FileText,
    color: 'text-red-700',
    bgColor: 'bg-red-100'
  },
  {
    id: 'waiting-parts',
    label: 'Menunggu Sparepart',
    status: 'pending',
    icon: Package,
    color: 'text-orange-700',
    bgColor: 'bg-orange-100'
  },
  {
    id: 'completed',
    label: 'Selesai',
    status: 'pending',
    icon: CheckCircle,
    color: 'text-emerald-700',
    bgColor: 'bg-emerald-100'
  }
];

// Map status dari database ke langkah-langkah service
const statusMapping: Record<string, number> = {
  'pending': 0,           // Belum Cek
  'checking': 1,          // Sedang Cek  
  'in-progress': 2,       // Sedang Dikerjakan
  'waiting-technician': 3, // Ditunggu MITRA Teknik
  'testing': 4,           // Sedang Tes
  'waiting-confirmation': 5, // Menunggu Konfirmasi
  'waiting-parts': 6,     // Menunggu Sparepart
  'completed': 7,         // Selesai
  'delivered': 7,         // Selesai (sudah diambil)
  'cancelled': -1         // Dibatalkan
};

export default function ServiceStatusTracker({ 
  isOpen, 
  onClose, 
  serviceNumber, 
  currentStatus 
}: ServiceStatusTrackerProps) {

  const getCurrentStepIndex = () => {
    return statusMapping[currentStatus] || 0;
  };

  const getStepStatus = (stepIndex: number): 'completed' | 'current' | 'pending' | 'waiting' => {
    const currentIndex = getCurrentStepIndex();

    if (currentStatus === 'cancelled') {
      return 'waiting';
    }

    if (stepIndex < currentIndex) {
      return 'completed';
    } else if (stepIndex === currentIndex) {
      return 'current';
    } else {
      return 'pending';
    }
  };

  const getStepColors = (step: ServiceStep, actualStatus: string) => {
    switch (actualStatus) {
      case 'completed':
        return {
          color: 'text-green-700',
          bgColor: 'bg-green-100',
          borderColor: 'border-green-500'
        };
      case 'current':
        return {
          color: step.color,
          bgColor: step.bgColor,
          borderColor: 'border-blue-500 ring-2 ring-blue-200'
        };
      case 'waiting':
        return {
          color: 'text-gray-500',
          bgColor: 'bg-gray-50',
          borderColor: 'border-gray-300'
        };
      default:
        return {
          color: 'text-gray-400',
          bgColor: 'bg-gray-50',
          borderColor: 'border-gray-200'
        };
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            <span>ðŸ“‹ Perbaikan Detail Status Proses Servis</span>
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={onClose}
              className="h-6 w-6 p-0"
            >
              <X className="h-4 w-4" />
            </Button>
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <div className="text-center text-sm text-gray-600">
            Silahkan pilih Detail Status untuk No. Servis <span className="font-bold text-blue-600">{serviceNumber}</span>
          </div>

          {/* Status Steps Grid */}
          <div className="grid grid-cols-2 gap-3">
            {serviceSteps.map((step, index) => {
              const stepStatus = getStepStatus(index);
              const colors = getStepColors(step, stepStatus);
              const Icon = step.icon;

              return (
                <div
                  key={step.id}
                  className={`
                    relative p-3 rounded-lg border-2 transition-all duration-200 cursor-pointer hover:shadow-md
                    ${colors.bgColor} ${colors.borderColor}
                  `}
                >
                  <div className="flex items-center space-x-3">
                    <div className={`
                      flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center
                      ${stepStatus === 'completed' ? 'bg-green-500 text-white' : 
                        stepStatus === 'current' ? 'bg-blue-500 text-white' : 
                        'bg-gray-300 text-gray-500'}
                    `}>
                      {stepStatus === 'completed' ? (
                        <CheckCircle className="w-5 h-5" />
                      ) : (
                        <Icon className="w-5 h-5" />
                      )}
                    </div>
                    <div className="flex-1">
                      <div className={`font-medium text-sm ${colors.color}`}>
                        {step.label}
                      </div>
                      {stepStatus === 'current' && (
                        <div className="text-xs text-blue-600 mt-1">
                          â† Status Saat Ini
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Status indicator */}
                  {stepStatus === 'current' && (
                    <div className="absolute -top-1 -right-1 w-3 h-3 bg-blue-500 rounded-full animate-pulse"></div>
                  )}
                </div>
              );
            })}
          </div>

          {/* Information Box */}
          <Card className="mt-6">
            <CardContent className="pt-4">
              <div className="text-sm text-gray-600">
                <strong>Informasi:</strong> Detail Status Proses Servis ini juga akan muncul saat Pelanggan 
                melakukan Cek Status (Tracking).
              </div>
            </CardContent>
          </Card>

          {/* Action Buttons */}
          <div className="flex justify-between pt-4 border-t">
            <Button 
              variant="outline" 
              onClick={onClose}
              className="text-red-600 border-red-200 hover:bg-red-50"
            >
              â† Batal
            </Button>
            <Button 
              onClick={onClose}
              className="bg-blue-500 hover:bg-blue-600 text-white"
            >
              âœ“ Perbaikan Detail Status
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { MessageCircle, QrCode, RefreshCw } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";

interface WhatsAppSettingsProps {
  storeConfig: any;
}

export function WhatsAppSettings({ storeConfig }: WhatsAppSettingsProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [testPhone, setTestPhone] = useState("");

  const whatsappEnabled = storeConfig?.whatsappEnabled || false;

  // Query WhatsApp status
  const { data: whatsappStatus, isLoading: statusLoading } = useQuery({
    queryKey: ['/api/whatsapp/status'],
    enabled: whatsappEnabled,
    refetchInterval: false, // Disable automatic polling to prevent infinite loop
    staleTime: 30000, // Cache for 30 seconds
    refetchOnWindowFocus: false,
  });

  // Enable/disable WhatsApp mutation
  const toggleWhatsAppMutation = useMutation({
    mutationFn: async (enabled: boolean) => {
      const response = await fetch(`/api/whatsapp/${enabled ? 'enable' : 'disable'}`, {
        method: 'POST',
        credentials: 'include',
      });
      if (!response.ok) {
        throw new Error('Failed to toggle WhatsApp');
      }
      return response.json();
    },
    onSuccess: () => {
      // Only invalidate WhatsApp status, not store config to prevent loop
      queryClient.invalidateQueries({ queryKey: ['/api/whatsapp/status'] });
      toast({
        title: "Berhasil",
        description: "Pengaturan WhatsApp berhasil diubah",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error", 
        description: error?.message || "Gagal mengubah pengaturan WhatsApp",
        variant: "destructive",
      });
    },
  });

  // Connect WhatsApp mutation
  const connectWhatsAppMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/whatsapp/connect', {
        method: 'POST',
        credentials: 'include',
      });
      if (!response.ok) {
        throw new Error('Failed to connect WhatsApp');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/whatsapp/status'] });
      toast({
        title: "Berhasil",
        description: "Proses koneksi WhatsApp dimulai. Scan QR code untuk menghubungkan.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Gagal menghubungkan WhatsApp",
        variant: "destructive",
      });
    },
  });

  // Disconnect WhatsApp mutation
  const disconnectWhatsAppMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/whatsapp/disconnect', {
        method: 'POST',
        credentials: 'include',
      });
      if (!response.ok) {
        throw new Error('Failed to disconnect WhatsApp');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/whatsapp/status'] });
      toast({
        title: "Berhasil",
        description: "WhatsApp berhasil diputuskan",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Gagal memutuskan WhatsApp",
        variant: "destructive",
      });
    },
  });

  // Test message mutation
  const testMessageMutation = useMutation({
    mutationFn: async (phone: string) => {
      const response = await fetch('/api/whatsapp/test-message', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ phoneNumber: phone }),
      });
      if (!response.ok) {
        throw new Error('Failed to send test message');
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Berhasil",
        description: "Pesan test berhasil dikirim",
      });
      setTestPhone("");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Gagal mengirim pesan test",
        variant: "destructive",
      });
    },
  });

  const handleTestMessage = () => {
    if (!testPhone.trim()) {
      toast({
        title: "Error",
        description: "Masukkan nomor telepon terlebih dahulu",
        variant: "destructive",
      });
      return;
    }
    testMessageMutation.mutate(testPhone);
  };

  const whatsappConnected = (whatsappStatus as any)?.connected || false;
  const connectionState = (whatsappStatus as any)?.state || 'close';
  const qrCode = (whatsappStatus as any)?.qrCode;

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <MessageCircle className="w-5 h-5 mr-2" />
          Integrasi WhatsApp
        </CardTitle>
        <CardDescription>
          Hubungkan WhatsApp untuk mengirim notifikasi otomatis ke pelanggan
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Enable/Disable WhatsApp */}
        <div className="flex items-center justify-between p-4 border rounded-lg">
          <div>
            <h4 className="font-medium">Status WhatsApp</h4>
            <p className="text-sm text-muted-foreground">
              {whatsappEnabled ? "WhatsApp integration aktif" : "WhatsApp integration nonaktif"}
            </p>
          </div>
          <Button
            variant={whatsappEnabled ? "destructive" : "default"}
            onClick={() => toggleWhatsAppMutation.mutate(!whatsappEnabled)}
            disabled={toggleWhatsAppMutation.isPending}
            data-testid={whatsappEnabled ? "button-disable-whatsapp" : "button-enable-whatsapp"}
          >
            {toggleWhatsAppMutation.isPending ? "Loading..." : whatsappEnabled ? "Nonaktifkan" : "Aktifkan"}
          </Button>
        </div>

        {/* Connection Status & Actions */}
        <div className="p-4 border rounded-lg">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h4 className="font-medium">Status Koneksi</h4>
              <p className="text-sm text-muted-foreground">
                {whatsappConnected ? (
                  <span className="text-green-600">âœ… Terhubung</span>
                ) : (
                  <span className="text-red-600">âŒ Tidak terhubung</span>
                )}
              </p>
              <p className="text-xs text-muted-foreground mt-1">
                State: {connectionState}
                {statusLoading && <RefreshCw className="w-3 h-3 ml-1 inline animate-spin" />}
              </p>
            </div>

            <div className="space-x-2">
              {!whatsappConnected ? (
                <Button
                  onClick={() => connectWhatsAppMutation.mutate()}
                  disabled={connectWhatsAppMutation.isPending}
                  data-testid="button-connect-whatsapp"
                >
                  {connectWhatsAppMutation.isPending ? (
                    <>
                      <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                      Connecting...
                    </>
                  ) : (
                    <>
                      <QrCode className="w-4 h-4 mr-2" />
                      Connect
                    </>
                  )}
                </Button>
              ) : (
                <Button
                  variant="destructive"
                  onClick={() => disconnectWhatsAppMutation.mutate()}
                  disabled={disconnectWhatsAppMutation.isPending}
                  data-testid="button-disconnect-whatsapp"
                >
                  {disconnectWhatsAppMutation.isPending ? "Disconnecting..." : "Disconnect"}
                </Button>
              )}
            </div>
          </div>

          {/* QR Code Display */}
          {qrCode && !whatsappConnected && (
            <div className="mt-4 p-4 border rounded-lg bg-gray-50 dark:bg-gray-800">
              <h5 className="font-medium mb-2 flex items-center">
                <QrCode className="w-4 h-4 mr-2" />
                Scan QR Code
              </h5>
              <p className="text-sm text-muted-foreground mb-4">
                Buka WhatsApp di ponsel â†’ Settings â†’ Linked Devices â†’ Link a Device â†’ Scan QR code di bawah
              </p>
              <div className="flex justify-center">
                <img 
                  src={qrCode} 
                  alt="WhatsApp QR Code" 
                  className="w-64 h-64 border rounded-lg"
                  data-testid="img-qr-code"
                />
              </div>
              <p className="text-xs text-center text-muted-foreground mt-2">
                QR code akan diperbarui secara otomatis setiap beberapa detik
              </p>
            </div>
          )}
        </div>

        {/* Test Message - Only show when connected */}
        {whatsappConnected && (
          <div className="p-4 border rounded-lg">
            <h4 className="font-medium mb-4">Test Pesan</h4>
            <div className="space-y-3">
              <div>
                <Label htmlFor="testPhone">Nomor Telepon (dengan kode negara)</Label>
                <Input
                  id="testPhone"
                  placeholder="contoh: 628123456789"
                  value={testPhone}
                  onChange={(e) => setTestPhone(e.target.value)}
                  data-testid="input-test-phone"
                />
                <p className="text-xs text-muted-foreground mt-1">
                  Format: 628xxxxxxxxx (tanpa tanda + atau spasi)
                </p>
              </div>
              <Button
                onClick={handleTestMessage}
                disabled={testMessageMutation.isPending || !testPhone.trim()}
                data-testid="button-send-test"
              >
                {testMessageMutation.isPending ? "Mengirim..." : "Kirim Test Pesan"}
              </Button>
            </div>
            <p className="text-sm text-muted-foreground mt-2">
              Kirim pesan test untuk memastikan koneksi WhatsApp berfungsi dengan baik
            </p>
          </div>
        )}

        {/* Feature Information */}
        <div className="space-y-4">
          <div className="border-t pt-4">
            <h4 className="font-medium mb-3">Fitur Notifikasi Otomatis</h4>
            <div className="space-y-2">
              <div className="flex items-center text-sm">
                <div className="w-2 h-2 bg-green-500 rounded-full mr-3"></div>
                <span>Service baru diterima</span>
              </div>
              <div className="flex items-center text-sm">
                <div className="w-2 h-2 bg-green-500 rounded-full mr-3"></div>
                <span>Status service berubah (sedang dikerjakan, selesai, dll)</span>
              </div>
              <div className="flex items-center text-sm">
                <div className="w-2 h-2 bg-green-500 rounded-full mr-3"></div>
                <span>Service siap diambil</span>
              </div>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { insertCustomerSchema } from "@shared/schema";
import { z } from "zod";

const customerFormSchema = insertCustomerSchema.extend({
  email: z.string().email("Email tidak valid").optional().or(z.literal("")),
  phone: z.string().optional(),
  address: z.string().optional(),
});

type CustomerFormData = z.infer<typeof customerFormSchema>;

interface CustomerCreateModalProps {
  open: boolean;
  onClose: () => void;
  onCustomerCreated?: (customer: any) => void;
}

export default function CustomerCreateModal({ 
  open, 
  onClose, 
  onCustomerCreated 
}: CustomerCreateModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<CustomerFormData>({
    resolver: zodResolver(customerFormSchema),
    defaultValues: {
      name: "",
      email: "",
      phone: "",
      address: "",
    },
  });

  const createCustomerMutation = useMutation({
    mutationFn: async (data: CustomerFormData) => {
      return await apiRequest('POST', '/api/customers', data);
    },
    onSuccess: (newCustomer) => {
      toast({
        title: "Success",
        description: "Customer berhasil ditambahkan",
      });

      // Invalidate customers query to refresh the list
      queryClient.invalidateQueries({ queryKey: ["/api/customers"] });

      // Reset form
      form.reset();

      // Call the callback with new customer data
      if (onCustomerCreated) {
        onCustomerCreated(newCustomer);
      }

      onClose();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Gagal menambahkan customer",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: CustomerFormData) => {
    // Clean up empty strings to null for optional fields
    const cleanData = {
      name: data.name,
      email: data.email || null,
      phone: data.phone || null,
      address: data.address || null,
    };

    createCustomerMutation.mutate(cleanData);
  };

  const handleClose = () => {
    form.reset();
    onClose();
  };

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>Tambah Customer Baru</DialogTitle>
        </DialogHeader>

        <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
          <div>
            <Label htmlFor="name">Nama *</Label>
            <Input
              id="name"
              placeholder="Masukkan nama customer"
              {...form.register("name")}
              data-testid="input-customer-name"
            />
            {form.formState.errors.name && (
              <p className="text-sm text-red-500 mt-1">
                {form.formState.errors.name.message}
              </p>
            )}
          </div>

          <div>
            <Label htmlFor="phone">Nomor Telepon</Label>
            <Input
              id="phone"
              placeholder="Contoh: 08123456789"
              {...form.register("phone")}
              data-testid="input-customer-phone"
            />
            {form.formState.errors.phone && (
              <p className="text-sm text-red-500 mt-1">
                {form.formState.errors.phone.message}
              </p>
            )}
          </div>

          <div>
            <Label htmlFor="email">Email</Label>
            <Input
              id="email"
              type="email"
              placeholder="contoh@email.com"
              {...form.register("email")}
              data-testid="input-customer-email"
            />
            {form.formState.errors.email && (
              <p className="text-sm text-red-500 mt-1">
                {form.formState.errors.email.message}
              </p>
            )}
          </div>

          <div>
            <Label htmlFor="address">Alamat</Label>
            <Textarea
              id="address"
              placeholder="Masukkan alamat lengkap"
              rows={3}
              {...form.register("address")}
              data-testid="input-customer-address"
            />
            {form.formState.errors.address && (
              <p className="text-sm text-red-500 mt-1">
                {form.formState.errors.address.message}
              </p>
            )}
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              disabled={createCustomerMutation.isPending}
              data-testid="button-cancel"
            >
              Batal
            </Button>
            <Button
              type="submit"
              disabled={createCustomerMutation.isPending}
              data-testid="button-save-customer"
            >
              {createCustomerMutation.isPending ? "Menyimpan..." : "Simpan"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

