export default function Sidebar() {
  // Use localStorage to persist sidebar state
  const [isCollapsed, setIsCollapsed] = useState(() => {
    const saved = localStorage.getItem('sidebarCollapsed');
    return saved ? JSON.parse(saved) : false;
  });
  const [location] = useLocation();
  const { user } = useAuth();
  const { toast } = useToast();

  // Get store config for app name - WITH BETTER CACHING
  const { data: storeConfig } = useQuery({
    queryKey: ['store-config'], // Changed key format
    queryFn: async () => {
      const response = await fetch('/api/store-config', { credentials: 'include' });
      if (!response.ok) return { name: 'LaptopPOS' };
      return response.json();
    },
    staleTime: Infinity,
    refetchInterval: false,
    refetchOnWindowFocus: false,
    refetchOnMount: false,
    retry: false,
  });

  const userRole = (user as any)?.role || "kasir";

  const logoutMutation = useMutation({
    mutationFn: async () => {
      return apiRequest('POST', '/api/auth/logout');
    },
    onSuccess: () => {
      toast({
        title: "Logout Berhasil",
        description: "Anda telah berhasil logout.",
      });
      // Reload to trigger authentication state update
      window.location.href = "/";
    },
    onError: (error: any) => {
      toast({
        title: "Error Logout",
        description: error.message || "Gagal logout",
        variant: "destructive",
      });
    },
  });

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  // Save sidebar state to localStorage when it changes
  const toggleCollapsed = () => {
    const newCollapsed = !isCollapsed;
    setIsCollapsed(newCollapsed);
    localStorage.setItem('sidebarCollapsed', JSON.stringify(newCollapsed));
  };

  const filteredNavigation = navigation.filter(item => 
    item.roles.includes(userRole)
  );

  return (
    <aside 
      className={cn(
        "transition-all duration-300 bg-card border-r border-border flex flex-col shadow-sm",
        isCollapsed ? "w-16" : "w-64"
      )}
      data-testid="sidebar"
    >
      <div className="p-4 border-b border-border">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
            <Laptop className="w-4 h-4 text-primary-foreground" />
          </div>
          {!isCollapsed && (
            <div className="transition-opacity duration-300">
              <h1 className="text-lg font-semibold text-foreground">{(storeConfig as any)?.name || 'LaptopPOS'}</h1>
              <p className="text-xs text-muted-foreground">Servis & Penjualan</p>
            </div>
          )}
        </div>
      </div>

      <nav className="flex-1 p-4 space-y-2 overflow-y-auto">
        {filteredNavigation.map((item) => {
          const isActive = location === item.href || (item.href !== "/" && location.startsWith(item.href));

          return (
            <Link key={item.name} href={item.href}>
              <div
                className={cn(
                  "flex items-center space-x-3 px-3 py-2 rounded-md text-sm font-medium transition-colors cursor-pointer",
                  isActive
                    ? "bg-primary text-primary-foreground"
                    : "text-muted-foreground hover:bg-muted hover:text-foreground"
                )}
                data-testid={`nav-item-${item.name.toLowerCase()}`}
              >
                <item.icon className="w-5 h-5 flex-shrink-0" />
                {!isCollapsed && (
                  <span className="transition-opacity duration-300">{item.name}</span>
                )}
              </div>
            </Link>
          );
        })}
      </nav>

      <div className="p-4 border-t border-border space-y-2">
        {/* User Info */}
        {!isCollapsed && (
          <div className="flex items-center space-x-3 px-3 py-2 rounded-md bg-muted/50">
            <User className="w-4 h-4 text-muted-foreground" />
            <div className="flex-1 min-w-0">
              <p className="text-sm font-medium text-foreground truncate">
                {(user as any)?.username || (user as any)?.firstName || "User"}
              </p>
              <p className="text-xs text-muted-foreground truncate">
                {userRole}
              </p>
            </div>
          </div>
        )}

        {/* Logout Button */}
        <Button
          variant="ghost" 
          size="sm"
          onClick={handleLogout}
          disabled={logoutMutation.isPending}
          className={cn(
            "w-full flex items-center text-red-600 hover:text-red-700 hover:bg-red-50 dark:hover:bg-red-950",
            isCollapsed ? "justify-center" : "justify-start space-x-3 px-3"
          )}
          data-testid="button-logout"
        >
          <LogOut className="w-4 h-4" />
          {!isCollapsed && (
            <span className="transition-opacity duration-300">
              {logoutMutation.isPending ? "Keluar..." : "Keluar"}
            </span>
          )}
        </Button>

        {/* Collapse Button */}
        <Button
          variant="ghost"
          size="sm"
          onClick={toggleCollapsed}
          className="w-full flex items-center justify-center"
          data-testid="button-collapse-sidebar"
        >
          {isCollapsed ? (
            <ChevronRight className="w-4 h-4" />
          ) : (
            <ChevronLeft className="w-4 h-4" />
          )}
        </Button>
      </div>
    </aside>
  );
}

import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { Printer, Download, X } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { format } from "date-fns";
import { id as idLocale } from "date-fns/locale";
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { useState } from "react";

interface ReceiptModalProps {
  open: boolean;
  onClose: () => void;
  transaction: any;
}

const paperSizes = {
  'a4': { name: 'A4 - Printer Biasa', width: 210, type: 'standard' },
  '58': { name: '58mm - Thermal Kecil', width: 58, type: 'thermal' },
  '80': { name: '80mm - Thermal Standar', width: 80, type: 'thermal' },
  '100': { name: '100mm - Thermal Besar', width: 100, type: 'thermal' },
} as const;

type PaperSize = keyof typeof paperSizes;

export default function ReceiptModal({ open, onClose, transaction }: ReceiptModalProps) {
  const [paperSize, setPaperSize] = useState<PaperSize>('a4');
  const [isGenerating, setIsGenerating] = useState(false);

  // Get store config for receipt header - WITH BETTER CACHING
  const { data: storeConfig } = useQuery({
    queryKey: ['store-config-receipt'],
    queryFn: async () => {
      const response = await fetch('/api/store-config', { credentials: 'include' });
      if (!response.ok) return { name: 'LaptopPOS', address: '', phone: '' };
      return response.json();
    },
    staleTime: Infinity,
    refetchInterval: false,
    refetchOnWindowFocus: false,
    retry: false,
  });

  if (!transaction) return null;

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const generatePDF = async () => {
    setIsGenerating(true);
    try {
      // Wait a bit to ensure content is fully loaded
      await new Promise(resolve => setTimeout(resolve, 500));

      const element = document.getElementById('purchase-receipt-content');
      if (!element) {
        console.error('Receipt element not found');
        alert('Error: Receipt element not found. Please try again.');
        return;
      }

      // Make sure element is visible
      element.style.display = 'block';
      element.style.visibility = 'visible';

      const canvas = await html2canvas(element, {
        scale: 2,
        useCORS: true,
        allowTaint: true,
        backgroundColor: '#ffffff',
        logging: true,
        width: element.scrollWidth,
        height: element.scrollHeight,
      });

      // Check if canvas has content
      if (canvas.width === 0 || canvas.height === 0) {
        console.error('Canvas is empty');
        alert('Error: Failed to capture receipt content. Please try again.');
        return;
      }

      const imgData = canvas.toDataURL('image/png');
      const pageWidth = paperSizes[paperSize].width;
      const pageHeight = paperSize === 'a4' ? 297 : (canvas.height / canvas.width) * pageWidth;

      const pdf = new jsPDF('p', 'mm', paperSize === 'a4' ? [148, 210] : [pageWidth, pageHeight]);
      if (paperSize === 'a4') {
        // For A5 size (half page), fit to page with margins
        const margin = 4;
        const availableWidth = 148 - (2 * margin); // A5 width
        const scaledHeight = (canvas.height / canvas.width) * availableWidth;
        pdf.addImage(imgData, 'PNG', margin, margin, availableWidth, scaledHeight);
      } else {
        pdf.addImage(imgData, 'PNG', 0, 0, pageWidth, pageHeight);
      }
      pdf.save(`Nota-Pembayaran-POS-${transaction.transactionNumber || transaction.id}-${paperSize === 'a4' ? 'A5' : pageWidth + 'mm'}.pdf`);
    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('Error generating PDF. Please try again or contact support.');
    } finally {
      setIsGenerating(false);
    }
  };

  const handlePrint = () => {
    try {
      // Ukuran kertas sesuai pilihan
      const pageWidth = paperSizes[paperSize].width;
      const fontSize = paperSize === 'a4' ? '12px' : 
                      paperSize === '58' ? '7px' : 
                      paperSize === '80' ? '8px' : '10px';

      const printStyle = `
        <style id="thermal-print-style">
          @media print {
            * { 
              visibility: hidden; 
              margin: 0 !important; 
              padding: 0 !important;
              box-sizing: border-box;
            }
            #purchase-receipt-content, 
            #purchase-receipt-content * { 
              visibility: visible; 
            }
            #purchase-receipt-content {
              position: absolute;
              left: 0;
              top: 0;
              width: ${paperSize === 'a4' ? '140mm' : `${pageWidth}mm`};
              max-width: ${paperSize === 'a4' ? '140mm' : `${pageWidth}mm`};
              font-family: ${paperSize === 'a4' ? 'Arial, sans-serif' : "'Courier New', monospace"};
              font-size: ${paperSize === 'a4' ? '11px' : fontSize};
              line-height: ${paperSize === 'a4' ? '1.4' : '1.0'};
              color: #000;
              background: #fff;
              page-break-inside: avoid;
              page-break-after: avoid;
              page-break-before: avoid;
              height: auto;
              max-height: ${paperSize === 'a4' ? '270mm' : '250mm'};
              padding: ${paperSize === 'a4' ? '10mm' : '2mm'};
            }
            .no-print { 
              display: none !important; 
            }
            @page {
              size: ${paperSize === 'a4' ? '148mm 210mm' : `${pageWidth}mm 300mm`};
              margin: ${paperSize === 'a4' ? '4mm' : '1mm'};
            }
            .text-center { text-align: center; }
            .font-bold { font-weight: bold; }
            .flex { display: flex; }
            .flex-1 { flex: 1; }
            .justify-between { justify-content: space-between; }
            .items-center { align-items: center; }
            .border-t { 
              border-top: 1px dashed #333; 
              margin: 1px 0 !important; 
            }
            .border-solid { border-style: solid; }
            .border-gray-800 { border-color: #333; }
            .text-gray-600 { color: #666; }
            .space-y-1 > * + * { margin-top: 1px !important; }
            .py-2 { padding: 1px 0 !important; }
            .my-2 { margin: 1px 0 !important; }
            .mb-2 { margin-bottom: 1px !important; }
            .mt-6 { margin-top: 2px !important; }
            h3 { font-size: ${fontSize}; margin: 1px 0 !important; }
            div { margin: 0 !important; padding: 0 !important; }
            p { margin: 0 !important; padding: 0 !important; }
          }
          @media screen {
            #thermal-print-style { display: none; }
          }
        </style>
      `;

      // Hapus style print lama jika ada
      const oldStyle = document.getElementById('thermal-print-style');
      if (oldStyle) oldStyle.remove();

      // Tambahkan CSS print baru
      document.head.insertAdjacentHTML('beforeend', printStyle);

      // Print
      window.print();

      // Restore setelah delay
      setTimeout(() => {
        const printStyleElement = document.getElementById('thermal-print-style');
        if (printStyleElement) {
          printStyleElement.remove();
        }
      }, 1000);

    } catch (error) {
      console.error('Print error:', error);
      alert('Terjadi kesalahan saat mencetak. Silakan coba lagi.');
    }
  };

  const getReceiptWidth = () => {
    switch (paperSize) {
      case 'a4': return 'max-w-[500px]';
      case '58': return 'max-w-[58mm]';
      case '80': return 'max-w-[80mm]';
      case '100': return 'max-w-[100mm]';
      default: return 'max-w-[500px]';
    }
  };

  const getTextSize = () => {
    switch (paperSize) {
      case 'a4': return 'text-sm';
      case '58': return 'text-xs';
      case '80': return 'text-sm';
      case '100': return 'text-base';
      default: return 'text-sm';
    }
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader className="no-print">
          <DialogTitle>Nota Pembayaran POS</DialogTitle>
          <Button
            variant="ghost"
            size="sm"
            onClick={onClose}
            className="absolute right-4 top-4"
            data-testid="button-close-receipt"
          >
            <X className="w-4 h-4" />
          </Button>
        </DialogHeader>

        <div className="space-y-4">
          {/* Paper Size Selector */}
          <div className="flex items-center gap-4 p-4 bg-gray-50 rounded-lg no-print">
            <Label htmlFor="paper-size" className="font-medium">Ukuran Kertas:</Label>
            <Select value={paperSize} onValueChange={(value: PaperSize) => setPaperSize(value)}>
              <SelectTrigger className="w-48" id="paper-size">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="a4">{paperSizes['a4'].name}</SelectItem>
                <SelectItem value="58">{paperSizes['58'].name}</SelectItem>
                <SelectItem value="80">{paperSizes['80'].name}</SelectItem>
                <SelectItem value="100">{paperSizes['100'].name}</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Action Buttons */}
          <div className="flex gap-3 no-print">
            <Button 
              onClick={handlePrint} 
              className="flex-1"
              data-testid="button-print-receipt"
            >
              <Printer className="w-4 h-4 mr-2" />
              Print
            </Button>
            <Button 
              variant="outline" 
              onClick={generatePDF}
              disabled={isGenerating}
              className="flex-1"
              data-testid="button-download-receipt"
            >
              <Download className="w-4 h-4 mr-2" />
              {isGenerating ? 'Generating...' : 'Download PDF'}
            </Button>
          </div>

          <Separator className="no-print" />

          {/* Preview Receipt */}
          <div className="flex justify-center">
            <div className={`${getReceiptWidth()} mx-auto bg-white border rounded-lg overflow-hidden shadow-lg`}>
              <div 
                className="p-4 font-mono" 
                id="purchase-receipt-content"
                style={{
                  minHeight: '300px',
                  display: 'block',
                  visibility: 'visible',
                  position: 'relative'
                }}
              >
                {/* Store Header */}
                <div className={`text-center ${getTextSize()} space-y-1`}>
                  <h3 className="font-bold" data-testid="text-store-name">
                    {(storeConfig as any)?.name || 'LaptopPOS Service Center'}
                  </h3>
                  {(storeConfig as any)?.address && (
                    <div data-testid="text-store-address">
                      {(storeConfig as any).address}
                    </div>
                  )}
                  <div className="space-y-0">
                    {(storeConfig as any)?.phone && (
                      <div data-testid="text-store-phone">Tel: {(storeConfig as any).phone}</div>
                    )}
                    {(storeConfig as any)?.email && (
                      <div data-testid="text-store-email">{(storeConfig as any).email}</div>
                    )}
                  </div>
                </div>

                <div className="border-t border-dashed border-gray-400 my-2"></div>

                {/* Transaction Info */}
                <div className={`${getTextSize()} space-y-1`}>
                  <div className="flex justify-between">
                    <span>No. Transaksi:</span>
                    <span data-testid="text-receipt-number">
                      {transaction.transactionNumber || transaction.id}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span>Tanggal:</span>
                    <span data-testid="text-receipt-date">
                      {format(new Date(transaction.createdAt || transaction.date || new Date()), 'dd/MM/yyyy HH:mm', { locale: idLocale })}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span>Kasir:</span>
                    <span data-testid="text-cashier-name">
                      {transaction.user?.firstName || 'Admin'}
                    </span>
                  </div>
                  {transaction.customer && (
                    <div className="flex justify-between">
                      <span>Pelanggan:</span>
                      <span data-testid="text-customer-name">
                        {transaction.customer.name || transaction.customer}
                      </span>
                    </div>
                  )}
                </div>

                <div className="border-t border-dashed border-gray-400 my-2"></div>

                {/* Items */}
                <div className={`${getTextSize()} space-y-1`}>
                  {transaction.items?.map((item: any, index: number) => (
                    <div key={index} className="space-y-1">
                      <div className="font-bold" data-testid={`item-name-${index}`}>
                        {item.product?.name || item.name}
                      </div>
                      <div className="flex justify-between">
                        <span>{item.quantity} x {formatCurrency(item.unitPrice || item.price)}</span>
                        <span data-testid={`item-total-${index}`}>
                          {formatCurrency(item.totalPrice || (item.price * item.quantity))}
                        </span>
                      </div>
                    </div>
                  )) || []}
                </div>

                <div className="border-t border-solid border-gray-800 my-2"></div>

                {/* Totals */}
                <div className={`${getTextSize()} space-y-1`}>
                  <div className="flex justify-between">
                    <span>Subtotal:</span>
                    <span data-testid="receipt-subtotal">
                      {formatCurrency(Number(transaction.subtotal || 0))}
                    </span>
                  </div>
                  {transaction.taxAmount && Number(transaction.taxAmount) > 0 && (
                    <div className="flex justify-between">
                      <span>Pajak:</span>
                      <span data-testid="receipt-tax">
                        {formatCurrency(Number(transaction.taxAmount))}
                      </span>
                    </div>
                  )}
                  {transaction.discountAmount && Number(transaction.discountAmount) > 0 && (
                    <div className="flex justify-between">
                      <span>Diskon:</span>
                      <span data-testid="receipt-discount">
                        -{formatCurrency(Number(transaction.discountAmount))}
                      </span>
                    </div>
                  )}
                  <div className={`flex justify-between font-bold ${getTextSize()}`}>
                    <span>TOTAL:</span>
                    <span data-testid="receipt-total">
                      {formatCurrency(Number(transaction.total || 0))}
                    </span>
                  </div>
                  {transaction.paymentMethod && (
                    <div className="flex justify-between">
                      <span>Pembayaran:</span>
                      <span data-testid="receipt-payment">
                        {transaction.paymentMethod === 'cash' ? 'Tunai' : 
                         transaction.paymentMethod === 'card' ? 'Kartu' : 
                         transaction.paymentMethod === 'transfer' ? 'Transfer' : 
                         transaction.paymentMethod}
                      </span>
                    </div>
                  )}
                </div>

                <div className="border-t border-dashed border-gray-400 my-2"></div>

                {/* Footer */}
                <div className={`text-center ${getTextSize()} text-gray-600 space-y-1 mt-6`}>
                  <div>Terima kasih atas pembelian Anda!</div>
                  <div>Barang yang sudah dibeli tidak dapat dikembalikan</div>
                  <div data-testid="text-print-date">Cetak: {format(new Date(), 'dd/MM/yy HH:mm', { locale: idLocale })}</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { Plus, Minus, X, Search, Barcode } from "lucide-react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import CustomerCreateModal from "@/components/customers/customer-create-modal";

interface TransactionModalProps {
  open: boolean;
  onClose: () => void;
  onComplete: (transaction: any) => void;
}

interface TransactionItem {
  productId: string;
  name: string;
  sellingPrice: number;
  quantity: number;
  stock?: number;
}

export default function TransactionModal({ open, onClose, onComplete }: TransactionModalProps) {
  const [selectedCustomer, setSelectedCustomer] = useState<any>(null);
  const [customerSearch, setCustomerSearch] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("cash");
  const [items, setItems] = useState<TransactionItem[]>([]);
  const [productSearch, setProductSearch] = useState("");
  const [selectedProducts, setSelectedProducts] = useState<any[]>([]);
  const [showCustomerCreateModal, setShowCustomerCreateModal] = useState(false);
  const { toast } = useToast();

  // Fetch products
  const { data: products = [], isLoading: productsLoading } = useQuery({
    queryKey: ["/api/products"],
  });

  // Fetch store config for tax rate - WITH PROPER REFRESH
  const { data: storeConfig } = useQuery({
    queryKey: ['store-config-transaction'],
    queryFn: async () => {
      const response = await fetch('/api/store-config', { credentials: 'include' });
      if (!response.ok) return { taxRate: 0 };
      return response.json();
    },
    staleTime: 5 * 60 * 1000, // 5 minutes instead of Infinity
    refetchOnWindowFocus: true,
    retry: false,
  });

  // Fetch customers
  const { data: customers = [], isLoading: customersLoading } = useQuery({
    queryKey: ["/api/customers", customerSearch],
    queryFn: async () => {
      const url = customerSearch ? `/api/customers?search=${encodeURIComponent(customerSearch)}` : '/api/customers';
      const response = await fetch(url);
      if (!response.ok) throw new Error('Failed to fetch customers');
      return response.json();
    },
  });

  // Filter customers based on search
  const filteredCustomers = customers.filter((customer: any) =>
    customer.name.toLowerCase().includes(customerSearch.toLowerCase()) ||
    customer.phone?.toLowerCase().includes(customerSearch.toLowerCase()) ||
    customer.email?.toLowerCase().includes(customerSearch.toLowerCase())
  );

  // Filter products based on search
  const filteredProducts = Array.isArray(products) ? products.filter((product: any) =>
    product.name.toLowerCase().includes(productSearch.toLowerCase()) ||
    product.barcode?.toLowerCase().includes(productSearch.toLowerCase())
  ) : [];

  // Create transaction mutation
  const createTransactionMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest('POST', '/api/transactions', data);
    },
    onSuccess: (data) => {
      toast({
        title: "Success",
        description: "Transaction completed successfully",
      });
      onComplete(data);
      resetForm();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to process transaction",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setSelectedCustomer(null);
    setCustomerSearch("");
    setPaymentMethod("cash");
    setItems([]);
    setProductSearch("");
    setSelectedProducts([]);
    setShowCustomerCreateModal(false);
  };

  const selectCustomer = (customer: any) => {
    setSelectedCustomer(customer);
    setCustomerSearch("");
  };

  const handleCustomerCreated = (newCustomer: any) => {
    selectCustomer(newCustomer);
  };

  const updateQuantity = (productId: string, delta: number) => {
    setItems(prev => prev.map(item => {
      if (item.productId === productId) {
        const newQuantity = Math.max(0, item.quantity + delta);
        // Check stock limit
        if (newQuantity > (item.stock || 0)) {
          toast({
            title: "Stock Insufficient",
            description: `Only ${item.stock} units available`,
            variant: "destructive",
          });
          return item;
        }
        return { ...item, quantity: newQuantity };
      }
      return item;
    }).filter(item => item.quantity > 0));
  };

  const removeItem = (productId: string) => {
    setItems(prev => prev.filter(item => item.productId !== productId));
  };

  const addProductToCart = (product: any) => {
    if (product.stock <= 0) {
      toast({
        title: "Out of Stock",
        description: `${product.name} is out of stock`,
        variant: "destructive",
      });
      return;
    }

    // Adding product to cart

    const existingItem = items.find(item => item.productId === product.id);
    if (existingItem) {
      updateQuantity(product.id, 1);
    } else {
      const sellingPrice = typeof product.sellingPrice === 'string' 
        ? parseFloat(product.sellingPrice) 
        : (product.sellingPrice || 0);

      const newItem: TransactionItem = {
        productId: product.id,
        name: product.name,
        sellingPrice: sellingPrice,
        quantity: 1,
        stock: product.stock,
      };
      // Item created successfully
      setItems(prev => [...prev, newItem]);
    }
    setProductSearch("");
  };

  const subtotal = items.reduce((sum, item) => sum + (item.sellingPrice * item.quantity), 0);
  const taxRate = Number(storeConfig?.taxRate || 11) / 100;
  const tax = subtotal * taxRate;
  const total = subtotal + tax;

  const handleProcessTransaction = () => {
    if (items.length === 0) {
      toast({
        title: "Error",
        description: "Please add at least one item to the transaction",
        variant: "destructive",
      });
      return;
    }

    if (!paymentMethod) {
      toast({
        title: "Error",
        description: "Please select a payment method",
        variant: "destructive",
      });
      return;
    }

    const transactionData = {
      transaction: {
        type: 'sale' as const,
        customerId: selectedCustomer?.id || null,
        paymentMethod: paymentMethod || 'cash',
        subtotal: Math.round(subtotal).toString(),
        taxAmount: Math.round(tax).toString(),
        discountAmount: '0.00',
        total: Math.round(total).toString(),
        notes: `POS Sale - ${items.length} items${selectedCustomer ? ` for ${selectedCustomer.name}` : ''}`,
      },
      items: items.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
        unitPrice: Math.round(item.sellingPrice).toString(),
        totalPrice: Math.round(item.sellingPrice * item.quantity).toString(),
      })),
    };

    createTransactionMutation.mutate(transactionData);
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-hidden">
        <DialogHeader>
          <DialogTitle>New Transaction</DialogTitle>
        </DialogHeader>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 overflow-y-auto">
          {/* Customer and Payment */}
          <div className="space-y-4">
            <div>
              <Label htmlFor="customer">Customer</Label>
              <div className="space-y-2">
                {selectedCustomer ? (
                  <div className="flex items-center justify-between p-3 bg-muted rounded-md">
                    <div>
                      <div className="font-medium">{selectedCustomer.name}</div>
                      <div className="text-sm text-muted-foreground">
                        {selectedCustomer.phone} â€¢ {selectedCustomer.email}
                      </div>
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setSelectedCustomer(null)}
                      data-testid="button-remove-customer"
                    >
                      <X className="w-4 h-4" />
                    </Button>
                  </div>
                ) : (
                  <>
                    <div className="relative">
                      <Input
                        id="customer"
                        placeholder="Search customer by name, phone, or email..."
                        value={customerSearch}
                        onChange={(e) => setCustomerSearch(e.target.value)}
                        data-testid="input-customer-search"
                      />
                      <Search className="absolute right-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                    </div>

                    {/* Customer Search Results */}
                    {customerSearch && (
                      <Card className="max-h-48 overflow-y-auto">
                        <CardContent className="p-2">
                          {customersLoading ? (
                            <div className="text-center py-4">Loading customers...</div>
                          ) : filteredCustomers.length > 0 ? (
                            <div className="space-y-1">
                              {filteredCustomers.slice(0, 8).map((customer: any) => (
                                <Button
                                  key={customer.id}
                                  variant="ghost"
                                  className="w-full justify-start h-auto p-3"
                                  onClick={() => selectCustomer(customer)}
                                  data-testid={`button-select-customer-${customer.id}`}
                                >
                                  <div className="text-left">
                                    <div className="font-medium">{customer.name}</div>
                                    <div className="text-sm text-muted-foreground">
                                      {customer.phone} â€¢ {customer.email}
                                    </div>
                                  </div>
                                </Button>
                              ))}
                            </div>
                          ) : (
                            <div className="text-center py-4 space-y-2">
                              <div className="text-muted-foreground">Tidak ada customer ditemukan</div>
                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => setShowCustomerCreateModal(true)}
                                data-testid="button-add-new-customer"
                              >
                                <Plus className="w-4 h-4 mr-2" />
                                Tambah Customer Baru
                              </Button>
                            </div>
                          )}
                        </CardContent>
                      </Card>
                    )}

                    <div className="text-xs text-muted-foreground">
                      Optional: Leave empty for walk-in customer
                    </div>
                  </>
                )}
              </div>
            </div>

            <div>
              <Label htmlFor="payment">Payment Method</Label>
              <Select value={paymentMethod} onValueChange={setPaymentMethod}>
                <SelectTrigger data-testid="select-payment-method">
                  <SelectValue placeholder="Select payment method" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="cash">Cash</SelectItem>
                  <SelectItem value="transfer">Bank Transfer</SelectItem>
                  <SelectItem value="credit">Credit Card</SelectItem>
                  <SelectItem value="debit">Debit Card</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Product Selection */}
          <div className="space-y-4">
            <div>
              <Label htmlFor="product">Add Products</Label>
              <div className="relative">
                <Input
                  id="product"
                  placeholder="Scan barcode or search product..."
                  value={productSearch}
                  onChange={(e) => setProductSearch(e.target.value)}
                  data-testid="input-product-search"
                />
                <Search className="absolute right-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              </div>

              {/* Product Search Results */}
              {productSearch && (
                <Card className="max-h-64 overflow-y-auto">
                  <CardContent className="p-2">
                    {productsLoading ? (
                      <div className="text-center py-4">Loading products...</div>
                    ) : filteredProducts.length > 0 ? (
                      <div className="space-y-1">
                        {filteredProducts.slice(0, 10).map((product: any) => (
                          <Button
                            key={product.id}
                            variant="ghost"
                            className="w-full justify-between h-auto p-3"
                            onClick={() => addProductToCart(product)}
                            disabled={product.stock <= 0}
                            data-testid={`button-add-product-${product.id}`}
                          >
                            <div className="text-left">
                              <div className="font-medium">{product.name}</div>
                              <div className="text-sm text-muted-foreground">
                                Stock: {product.stock} | Rp {product.sellingPrice?.toLocaleString('id-ID')}
                              </div>
                            </div>
                            <Plus className="w-4 h-4" />
                          </Button>
                        ))}
                      </div>
                    ) : (
                      <div className="text-center py-4 text-muted-foreground">
                        No products found
                      </div>
                    )}
                  </CardContent>
                </Card>
              )}
            </div>

            {/* Selected Items */}
            <div className="space-y-2 max-h-40 overflow-y-auto">
              {items.map((item) => (
                <Card key={item.productId} className="p-3">
                  <div className="flex justify-between items-center text-sm">
                    <div className="flex-1">
                      <div className="font-medium">{item.name}</div>
                      <div className="text-xs text-muted-foreground">
                        Rp {(item.sellingPrice || 0).toLocaleString('id-ID')} Ã— {item.quantity} = Rp {(item.sellingPrice * item.quantity).toLocaleString('id-ID')}
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => updateQuantity(item.productId, -1)}
                        data-testid={`button-decrease-${item.productId}`}
                      >
                        <Minus className="w-3 h-3" />
                      </Button>
                      <span data-testid={`text-quantity-${item.productId}`} className="w-8 text-center">
                        {item.quantity}
                      </span>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => updateQuantity(item.productId, 1)}
                        data-testid={`button-increase-${item.productId}`}
                        disabled={item.quantity >= (item.stock || 0)}
                      >
                        <Plus className="w-3 h-3" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => removeItem(item.productId)}
                        data-testid={`button-remove-${item.productId}`}
                      >
                        <X className="w-3 h-3" />
                      </Button>
                    </div>
                  </div>
                </Card>
              ))}

              {items.length === 0 && (
                <div className="text-center py-8 text-muted-foreground">
                  No items added yet. Search and add products above.
                </div>
              )}
            </div>

            {/* Transaction Summary */}
            <Card className="p-4">
              <div className="space-y-2">
                <div className="flex justify-between text-sm">
                  <span>Subtotal:</span>
                  <span data-testid="text-subtotal">Rp {subtotal.toLocaleString('id-ID')}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span>Tax ({((storeConfig as any)?.taxRate || 11)}%):</span>
                  <span data-testid="text-tax">Rp {tax.toLocaleString('id-ID')}</span>
                </div>
                <div className="flex justify-between font-semibold border-t pt-2">
                  <span>Total:</span>
                  <span data-testid="text-total">Rp {total.toLocaleString('id-ID')}</span>
                </div>
              </div>
            </Card>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex justify-end space-x-3 pt-6 border-t">
          <Button 
            variant="outline" 
            onClick={onClose} 
            disabled={createTransactionMutation.isPending}
            data-testid="button-cancel"
          >
            Cancel
          </Button>
          <Button 
            onClick={handleProcessTransaction}
            disabled={createTransactionMutation.isPending || items.length === 0}
            data-testid="button-process-transaction"
          >
            {createTransactionMutation.isPending ? "Processing..." : "Process Transaction"}
          </Button>
        </div>
      </DialogContent>

      <CustomerCreateModal
        open={showCustomerCreateModal}
        onClose={() => setShowCustomerCreateModal(false)}
        onCustomerCreated={handleCustomerCreated}
      />
    </Dialog>
  );
}

import { useState, useMemo } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Plus, Minus, Package, AlertTriangle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { Product } from "@shared/schema";

interface ServicePart {
  productId: string;
  productName: string;
  quantity: number;
  unitPrice: string;
  totalPrice: string;
  stock: number;
}

interface ServicePartsSelectorProps {
  parts: ServicePart[];
  onPartsChange: (parts: ServicePart[]) => void;
  laborCost: number;
}

export function ServicePartsSelector({ parts, onPartsChange, laborCost }: ServicePartsSelectorProps) {
  const { toast } = useToast();
  const [selectedProductId, setSelectedProductId] = useState<string>("");
  const [quantity, setQuantity] = useState<number>(1);

  // Fetch products
  const { data: products = [] } = useQuery<Product[]>({
    queryKey: ['/api/products'],
    queryFn: () => fetch('/api/products').then(res => res.json())
  });

  // Filter products that have stock
  const availableProducts = useMemo(() => {
    return products.filter(product => product.stock > 0 && product.isActive);
  }, [products]);

  const selectedProduct = useMemo(() => {
    return availableProducts.find(p => p.id === selectedProductId);
  }, [availableProducts, selectedProductId]);

  const totalPartsCost = useMemo(() => {
    return parts.reduce((sum, part) => sum + parseFloat(part.totalPrice), 0);
  }, [parts]);

  const totalCost = useMemo(() => {
    return laborCost + totalPartsCost;
  }, [laborCost, totalPartsCost]);

  const handleAddPart = () => {
    if (!selectedProduct) {
      toast({
        title: "Error",
        description: "Pilih produk terlebih dahulu",
        variant: "destructive"
      });
      return;
    }

    if (quantity > selectedProduct.stock) {
      toast({
        title: "Error",
        description: `Stock tidak cukup. Tersedia: ${selectedProduct.stock}`,
        variant: "destructive"
      });
      return;
    }

    // Check if product already added
    const existingPartIndex = parts.findIndex(part => part.productId === selectedProduct.id);

    if (existingPartIndex >= 0) {
      const existingPart = parts[existingPartIndex];
      const newQuantity = existingPart.quantity + quantity;

      if (newQuantity > selectedProduct.stock) {
        toast({
          title: "Error",
          description: `Total quantity melebihi stock. Tersedia: ${selectedProduct.stock}, Sudah dipilih: ${existingPart.quantity}`,
          variant: "destructive"
        });
        return;
      }

      const unitPrice = selectedProduct.sellingPrice || "0";
      const newTotalPrice = (parseFloat(unitPrice) * newQuantity).toString();

      const updatedParts = parts.map((part, index) => 
        index === existingPartIndex 
          ? { ...part, quantity: newQuantity, totalPrice: newTotalPrice }
          : part
      );
      onPartsChange(updatedParts);
    } else {
      const unitPrice = selectedProduct.sellingPrice || "0";
      const totalPrice = (parseFloat(unitPrice) * quantity).toString();

      const newPart: ServicePart = {
        productId: selectedProduct.id,
        productName: selectedProduct.name,
        quantity,
        unitPrice,
        totalPrice,
        stock: selectedProduct.stock
      };

      onPartsChange([...parts, newPart]);
    }

    // Reset form
    setSelectedProductId("");
    setQuantity(1);
  };

  const handleRemovePart = (productId: string) => {
    onPartsChange(parts.filter(part => part.productId !== productId));
  };

  const handleQuantityChange = (productId: string, newQuantity: number) => {
    const part = parts.find(p => p.productId === productId);
    if (!part) return;

    const product = availableProducts.find(p => p.id === productId);
    if (!product) return;

    if (newQuantity > product.stock) {
      toast({
        title: "Error",
        description: `Quantity melebihi stock. Tersedia: ${product.stock}`,
        variant: "destructive"
      });
      return;
    }

    if (newQuantity <= 0) {
      handleRemovePart(productId);
      return;
    }

    const newTotalPrice = (parseFloat(part.unitPrice) * newQuantity).toString();

    const updatedParts = parts.map(p => 
      p.productId === productId 
        ? { ...p, quantity: newQuantity, totalPrice: newTotalPrice }
        : p
    );
    onPartsChange(updatedParts);
  };

  return (
    <div className="space-y-4">
      {/* Add Parts Section */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Package className="w-4 h-4" />
            Tambah Spare Parts
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-12 gap-4 items-end">
            <div className="col-span-6">
              <Label htmlFor="product-select">Pilih Produk</Label>
              <Select value={selectedProductId} onValueChange={setSelectedProductId}>
                <SelectTrigger>
                  <SelectValue placeholder="Pilih spare part..." />
                </SelectTrigger>
                <SelectContent>
                  {availableProducts.map((product) => (
                    <SelectItem key={product.id} value={product.id}>
                      <div className="flex justify-between items-center w-full">
                        <span>{product.name}</span>
                        <div className="flex items-center gap-2 ml-4">
                          <Badge variant="outline" className="text-xs">
                            Stock: {product.stock}
                          </Badge>
                          <Badge variant="secondary" className="text-xs">
                            Rp {parseFloat(product.sellingPrice || "0").toLocaleString("id-ID")}
                          </Badge>
                        </div>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="col-span-2">
              <Label htmlFor="quantity">Quantity</Label>
              <Input
                id="quantity"
                type="number"
                min="1"
                max={selectedProduct?.stock || 1}
                value={quantity}
                onChange={(e) => setQuantity(parseInt(e.target.value) || 1)}
              />
            </div>
            <div className="col-span-2">
              <Label>Harga Satuan</Label>
              <div className="p-2 bg-muted rounded text-sm">
                {selectedProduct 
                  ? `Rp ${parseFloat(selectedProduct.sellingPrice || "0").toLocaleString("id-ID")}`
                  : "-"
                }
              </div>
            </div>
            <div className="col-span-2">
              <Button 
                onClick={handleAddPart} 
                disabled={!selectedProduct}
                className="w-full"
              >
                <Plus className="w-4 h-4 mr-2" />
                Tambah
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Selected Parts List */}
      {parts.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Parts yang Digunakan</CardTitle>
          </CardHeader>
          <CardContent>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Produk</TableHead>
                  <TableHead>Stock Tersedia</TableHead>
                  <TableHead>Quantity</TableHead>
                  <TableHead>Harga Satuan</TableHead>
                  <TableHead>Total</TableHead>
                  <TableHead>Action</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {parts.map((part) => (
                  <TableRow key={part.productId}>
                    <TableCell>{part.productName}</TableCell>
                    <TableCell>
                      <Badge variant={part.stock <= 5 ? "destructive" : "secondary"}>
                        {part.stock} unit
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleQuantityChange(part.productId, part.quantity - 1)}
                        >
                          <Minus className="w-3 h-3" />
                        </Button>
                        <span className="min-w-[2rem] text-center">{part.quantity}</span>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleQuantityChange(part.productId, part.quantity + 1)}
                        >
                          <Plus className="w-3 h-3" />
                        </Button>
                      </div>
                    </TableCell>
                    <TableCell>
                      Rp {parseFloat(part.unitPrice).toLocaleString("id-ID")}
                    </TableCell>
                    <TableCell>
                      Rp {parseFloat(part.totalPrice).toLocaleString("id-ID")}
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleRemovePart(part.productId)}
                      >
                        <Minus className="w-4 h-4" />
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      )}

      {/* Cost Summary */}
      <Card>
        <CardHeader>
          <CardTitle>Ringkasan Biaya</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            <div className="flex justify-between">
              <span>Biaya Tenaga Kerja:</span>
              <span>Rp {laborCost.toLocaleString("id-ID")}</span>
            </div>
            <div className="flex justify-between">
              <span>Biaya Parts:</span>
              <span>Rp {totalPartsCost.toLocaleString("id-ID")}</span>
            </div>
            <div className="border-t pt-2">
              <div className="flex justify-between font-bold text-lg">
                <span>Total Biaya:</span>
                <span>Rp {totalCost.toLocaleString("id-ID")}</span>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {availableProducts.length === 0 && (
        <Card>
          <CardContent className="flex items-center justify-center py-8">
            <div className="text-center">
              <AlertTriangle className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">
                Tidak ada produk dengan stock tersedia
              </p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

