  // Product Batches
  async getProductBatches(productId?: string): Promise<ProductBatch[]> {
    if (productId) {
      return await db.select().from(productBatches).where(eq(productBatches.productId, productId)).orderBy(desc(productBatches.receivedDate));
    }
