            ),
            gte(financialRecords.createdAt, startDate),
            lte(financialRecords.createdAt, endDate)
          )
        );

      const [serviceCostResult] = await db
        .select({ total: sum(financialRecords.amount) })
        .from(financialRecords)
        .where(
