import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import WizardSidebar from "@/components/installation/wizard-sidebar";
import InstallationProgress from "@/components/installation/installation-progress";
import TransactionDiscountSetup from "@/components/installation/transaction-discount-setup";
import OnlineTrackingSetup from "@/components/installation/online-tracking-setup";
import WhatsAppTroubleshooting from "@/components/installation/whatsapp-troubleshooting";
import PrintLayoutSetup from "@/components/installation/print-layout-setup";
import DailyExpenseSetup from "@/components/installation/daily-expense-setup";
import { Button } from "@/components/ui/button";
import type { InstallationStep } from "@shared/schema";

export default function InstallationWizard() {
  const [activeStep, setActiveStep] = useState("transaction-discount");

  // Fetch installation steps
  const { data: steps = [], isLoading } = useQuery<InstallationStep[]>({
    queryKey: ["/api/installation/steps"],
  });

  const calculateOverallProgress = () => {
    if (steps.length === 0) return 0;
    const totalProgress = steps.reduce((sum, step) => sum + step.progress, 0);
    return Math.round(totalProgress / steps.length);
  };

  const handleFinalizeSetup = () => {
    console.log("Finalizing setup...");
    // This would typically complete the installation process
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading installation wizard...</p>
        </div>
      </div>
    );
  }

  const renderActiveComponent = () => {
    switch (activeStep) {
      case "transaction-discount":
        return <TransactionDiscountSetup />;
      case "online-tracking":
        return <OnlineTrackingSetup />;
      case "whatsapp-api":
        return <WhatsAppTroubleshooting />;
      case "print-layouts":
        return <PrintLayoutSetup />;
      case "daily-expenses":
        return <DailyExpenseSetup />;
      default:
        return <TransactionDiscountSetup />;
    }
  };

  return (
    <div className="min-h-screen flex bg-background">
      {/* Sidebar Navigation */}
      <WizardSidebar
        steps={steps}
        activeStep={activeStep}
        onStepChange={setActiveStep}
      />

      {/* Main Content Area */}
      <div className="flex-1 overflow-auto">
        <InstallationProgress progress={calculateOverallProgress()} />

        <main className="p-6 space-y-6">
          <div className="slide-in-up">
            {renderActiveComponent()}
          </div>

          {/* Installation Complete */}
          <div className="bg-gradient-to-r from-green-50 to-blue-50 dark:from-green-950 dark:to-blue-950 border border-green-200 dark:border-green-800 rounded-lg p-6">
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 bg-green-100 dark:bg-green-900 rounded-full flex items-center justify-center">
                <i className="fas fa-check text-green-600 dark:text-green-400 text-xl"></i>
              </div>
              <div>
                <h3 className="text-lg font-semibold text-foreground">Installation Nearly Complete!</h3>
                <p className="text-sm text-muted-foreground mt-1">
                  All major features have been configured. Complete the WhatsApp API optimization to finish the setup.
                </p>
              </div>
              <div className="flex-shrink-0">
                <Button
                  onClick={handleFinalizeSetup}
                  className="bg-green-600 hover:bg-green-700 text-white px-6 py-3"
                  data-testid="button-finalize-setup"
                >
                  Finalize Setup
                </Button>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

server/controllers/saasController.ts.content:
import { Request, Response } from 'express';
import { db } from '../db';
import { clients, subscriptions, payments, planFeatures } from '../../shared/saas-schema';
import { insertClientSchema, insertSubscriptionSchema } from '../../shared/saas-schema';
import { eq, and, desc, count } from 'drizzle-orm';
import bcrypt from 'bcryptjs';
import { storage } from '../storage';

export class SaasController {

  // Client registration
  static async registerClient(req: Request, res: Response) {
    try {
      const validatedData = insertClientSchema.parse(req.body);

      // Check if subdomain is available
      const existingClient = await db
        .select()
        .from(clients)
        .where(eq(clients.subdomain, validatedData.subdomain))
        .limit(1);

      if (existingClient.length > 0) {
        return res.status(400).json({
          error: 'Subdomain not available',
          message: 'This subdomain is already taken. Please choose another.'
        });
      }

      // Check if email is already registered
      const existingEmail = await db
        .select()
        .from(clients)
        .where(eq(clients.email, validatedData.email))
        .limit(1);

      if (existingEmail.length > 0) {
        return res.status(400).json({
          error: 'Email already registered',
          message: 'This email is already associated with another account.'
        });
      }

      // Create client
      const newClient = await db
        .insert(clients)
        .values({
          ...validatedData,
          status: 'trial'
        })
        .returning();

      // Create trial subscription (7 days)
      const trialEndDate = new Date();
      trialEndDate.setDate(trialEndDate.getDate() + 7);

      await db.insert(subscriptions).values({
        clientId: newClient[0].id,
        plan: 'basic',
        startDate: new Date(),
        endDate: trialEndDate,
        paymentStatus: 'paid',
        amount: 0,
        currency: 'IDR',
        autoRenew: false
      });

      // Create default admin user for the tenant
      const hashedPassword = await bcrypt.hash('admin123', 10);
      await storage.createUser({
        id: `admin-${newClient[0].id}`,
        clientId: newClient[0].id, // Add client_id to user
        username: 'admin',
        email: validatedData.email,
        firstName: 'Admin',
        lastName: 'User',
        password: hashedPassword,
        role: 'admin',
        isActive: true
      });

      res.status(201).json({
        success: true,
        client: {
          id: newClient[0].id,
          name: newClient[0].name,
          subdomain: newClient[0].subdomain,
          status: newClient[0].status
        },
        message: 'Client registered successfully. You have 7 days of free trial.',
        accessUrl: `http://${validatedData.subdomain}.laptoppos.com`
      });

    } catch (error) {
      console.error('Client registration error:', error);
      res.status(500).json({
        error: 'Registration failed',
        message: 'Unable to create client account. Please try again.'
      });
    }
  }

  // Get client info
  static async getClientInfo(req: Request, res: Response) {
    try {
      if (!req.tenant) {
        return res.status(400).json({ error: 'No tenant context' });
      }

      const clientInfo = await db
        .select()
        .from(clients)
        .where(eq(clients.id, req.tenant.id))
        .limit(1);

      if (!clientInfo.length) {
        return res.status(404).json({ error: 'Client not found' });
      }

      // Get active subscription
      const subscription = await db
        .select()
        .from(subscriptions)
        .where(eq(subscriptions.clientId, req.tenant.id))
        .orderBy(desc(subscriptions.createdAt))
        .limit(1);

      res.json({
        client: clientInfo[0],
        subscription: subscription[0] || null
      });

    } catch (error) {
      console.error('Get client info error:', error);
      res.status(500).json({ error: 'Unable to fetch client information' });
    }
  }

  // Create subscription
  static async createSubscription(req: Request, res: Response) {
    try {
      if (!req.tenant) {
        return res.status(400).json({ error: 'No tenant context' });
      }

      const { plan, duration } = req.body; // duration in months

      // Define pricing
      const pricing = {
        basic: { monthly: 99000, yearly: 990000 },
        pro: { monthly: 199000, yearly: 1990000 },
        premium: { monthly: 399000, yearly: 3990000 }
      };

      if (!pricing[plan as keyof typeof pricing]) {
        return res.status(400).json({ error: 'Invalid plan' });
      }

      const isYearly = duration === 12;
      const planPricing = pricing[plan as keyof typeof pricing];
      const amount = isYearly ? planPricing.yearly : planPricing.monthly;

      // Calculate dates
      const startDate = new Date();
      const endDate = new Date();
      endDate.setMonth(endDate.getMonth() + duration);

      // Create subscription
      const newSubscription = await db
        .insert(subscriptions)
        .values({
          clientId: req.tenant.id,
          plan,
          startDate,
          endDate,
          paymentStatus: 'pending',
          amount,
          currency: 'IDR',
          autoRenew: true
        })
        .returning();

      res.status(201).json({
        success: true,
        subscription: newSubscription[0],
        paymentUrl: `/payment?subscription=${newSubscription[0].id}`,
        message: 'Subscription created. Please complete payment to activate.'
      });

    } catch (error) {
      console.error('Create subscription error:', error);
      res.status(500).json({ error: 'Unable to create subscription' });
    }
  }

  // Mock payment endpoint
  static async processPayment(req: Request, res: Response) {
    try {
      const { subscriptionId, paymentMethod = 'bank_transfer' } = req.body;

      // Get subscription
      const subscription = await db
        .select()
        .from(subscriptions)
        .where(eq(subscriptions.id, subscriptionId))
        .limit(1);

      if (!subscription.length) {
        return res.status(404).json({ error: 'Subscription not found' });
      }

      const sub = subscription[0];

      // Create payment record
      const payment = await db
        .insert(payments)
        .values({
          subscriptionId: sub.id,
          clientId: sub.clientId,
          amount: sub.amount,
          currency: sub.currency,
          status: 'paid', // Mock payment always succeeds
          paymentMethod,
          transactionId: `mock_${Date.now()}`,
          paidAt: new Date()
        })
        .returning();

      // Update subscription status
      await db
        .update(subscriptions)
        .set({ 
          paymentStatus: 'paid',
          updatedAt: new Date()
        })
        .where(eq(subscriptions.id, sub.id));

      // Update client status to active
      await db
        .update(clients)
        .set({ 
          status: 'active',
          updatedAt: new Date()
        })
        .where(eq(clients.id, sub.clientId));

      res.json({
        success: true,
        payment: payment[0],
        message: 'Payment processed successfully. Your subscription is now active!'
      });

    } catch (error) {
      console.error('Payment processing error:', error);
      res.status(500).json({ error: 'Payment processing failed' });
    }
  }

  // Get subscription plans
  static async getPlans(req: Request, res: Response) {
    try {
      const plans = await db
        .select()
        .from(planFeatures)
        .orderBy(planFeatures.plan);

      // Group features by plan
      const planData = {
        basic: {
          name: 'Basic',
          price: { monthly: 99000, yearly: 990000 },
          features: plans.filter(p => p.plan === 'basic')
        },
        pro: {
          name: 'Pro', 
          price: { monthly: 199000, yearly: 1990000 },
          features: plans.filter(p => p.plan === 'pro')
        },
        premium: {
          name: 'Premium',
          price: { monthly: 399000, yearly: 3990000 },
          features: plans.filter(p => p.plan === 'premium')
        }
      };

      res.json({ plans: planData });

    } catch (error) {
      console.error('Get plans error:', error);
      res.status(500).json({ error: 'Unable to fetch plans' });
    }
  }

  // Super Admin: Get all clients
  static async getAllClients(req: Request, res: Response) {
    try {
      const page = parseInt(req.query.page as string) || 1;
      const limit = parseInt(req.query.limit as string) || 20;
      const offset = (page - 1) * limit;

      const allClients = await db
        .select()
        .from(clients)
        .limit(limit)
        .offset(offset)
        .orderBy(desc(clients.createdAt));

      const totalClients = await db
        .select({ count: count() })
        .from(clients);

      res.json({
        clients: allClients,
        pagination: {
          page,
          limit,
          total: totalClients[0].count,
          pages: Math.ceil(totalClients[0].count / limit)
        }
      });

    } catch (error) {
      console.error('Get all clients error:', error);
      res.status(500).json({ error: 'Unable to fetch clients' });
    }
  }

  // Super Admin: Update client status
  static async updateClientStatus(req: Request, res: Response) {
    try {
      const { clientId } = req.params;
      const { status } = req.body;

      if (!['active', 'suspended', 'expired', 'trial'].includes(status)) {
        return res.status(400).json({ error: 'Invalid status' });
      }

      await db
        .update(clients)
        .set({ 
          status,
          updatedAt: new Date()
        })
        .where(eq(clients.id, clientId));

      res.json({
        success: true,
        message: `Client status updated to ${status}`
      });

    } catch (error) {
      console.error('Update client status error:', error);
      res.status(500).json({ error: 'Unable to update client status' });
    }
  }

  // Super Admin: Get dashboard stats
  static async getDashboardStats(req: Request, res: Response) {
    try {
      const totalClients = await db
        .select({ count: count() })
        .from(clients);

      const activeClients = await db
        .select({ count: count() })
        .from(clients)
        .where(eq(clients.status, 'active'));

      const trialClients = await db
        .select({ count: count() })
        .from(clients)
        .where(eq(clients.status, 'trial'));

      const totalRevenue = await db
        .select()
        .from(payments)
        .where(eq(payments.status, 'paid'));

      const revenue = totalRevenue.reduce((sum, payment) => sum + payment.amount, 0);

      res.json({
        stats: {
          totalClients: totalClients[0].count,
          activeClients: activeClients[0].count,
          trialClients: trialClients[0].count,
          suspendedClients: totalClients[0].count - activeClients[0].count - trialClients[0].count,
          totalRevenue: revenue,
          avgRevenuePerClient: activeClients[0].count > 0 ? revenue / activeClients[0].count : 0
        }
      });

    } catch (error) {
      console.error('Get dashboard stats error:', error);
      res.status(500).json({ error: 'Unable to fetch dashboard stats' });
    }
  }
}

server/middleware/tenant.ts.content:
import { Request, Response, NextFunction } from 'express';
import { db } from '../db';
import { clients, subscriptions } from '../../shared/saas-schema';
import { eq, and } from 'drizzle-orm';

// Extend Express Request to include tenant info
declare global {
  namespace Express {
    interface Request {
      tenant?: {
        id: string;
        subdomain: string;
        name: string;
        status: string;
        subscription?: any;
        settings?: any;
      };
      isSuperAdmin?: boolean;
    }
  }
}

// Middleware to detect tenant from subdomain
export const tenantMiddleware = async (req: Request, res: Response, next: NextFunction) => {
  try {
    const host = req.headers.host || '';
    console.log('Host header:', host);

    // IMMEDIATE CHECK: In development, if accessing admin routes, grant super admin access
    if ((host.includes('.replit.dev') || host.includes('.replit.app') || host.includes('localhost')) && req.path.startsWith('/api/admin')) {
      console.log('Development admin route detected, granting super admin access');
      req.isSuperAdmin = true;
      return next();
    }

    // Extract subdomain
    let subdomain = '';

    // Handle different environments
    if (host.includes('localhost') || host.includes('127.0.0.1')) {
      // Development: check for subdomain in query param or header, skip tenant detection if not specified
      subdomain = req.query.tenant as string || req.headers['x-tenant'] as string;

      // If no tenant specified in localhost, skip tenant middleware entirely
      if (!subdomain) {
        console.log('Localhost development: No tenant specified, skipping tenant middleware');
        return next();
      }
    } else if (host.includes('.ngrok.io') || host.includes('.ngrok-free.app')) {
      // Ngrok: Use query param or header for tenant
      subdomain = req.query.tenant as string || req.headers['x-tenant'] as string || 'demo';
    } else if (host.includes('.replit.dev') || host.includes('.replit.app')) {
      // Replit development environment: Use query param or header, or skip tenant detection
      subdomain = req.query.tenant as string || req.headers['x-tenant'] as string;

      // If no tenant specified in Replit, skip tenant middleware entirely
      if (!subdomain) {
        console.log('Replit development: No tenant specified, skipping tenant middleware');
        return next();
      }
    } else {
      // Production: extract subdomain from domain
      const parts = host.split('.');

      // Handle known base domains correctly
      const knownBaseDomains = ['profesionalservis.my.id'];
      const isKnownBaseDomain = knownBaseDomains.some(baseDomain => host === baseDomain || host === `www.${baseDomain}`);

      if (isKnownBaseDomain) {
        // This is the main domain, not a subdomain
        subdomain = 'main';
      } else if (parts.length >= 4) {
        // Real subdomain: subdomain.profesionalservis.my.id
        subdomain = parts[0];
      } else if (parts.length === 3) {
        // Could be subdomain.domain.com or domain.co.id
        // Check if it matches our base domain pattern
        const possibleBaseDomain = parts.slice(1).join('.');
        if (knownBaseDomains.includes(possibleBaseDomain)) {
          subdomain = parts[0]; // Real subdomain like client.profesionalservis.my.id
        } else {
          subdomain = 'main'; // Main domain like domain.co.id
        }
      } else {
        // Main domain - might be super admin or landing page
        subdomain = 'main';
      }
    }

    console.log('Detected subdomain:', subdomain);

    // Special handling for super admin routes
    if (subdomain === 'admin' || subdomain === 'main' || req.path.startsWith('/api/admin')) {
      req.isSuperAdmin = true;
      return next();
    }

    // Skip tenant detection for certain routes and original app routes
    const skipRoutes = [
      '/api/auth', 
      '/api/health', 
      '/api/saas/register', 
      '/api/saas/plans', 
      '/api/saas/payment', 
      '/api/payment-webhook',
      '/api/users',
      '/api/customers',
      '/api/products',
      '/api/transactions',
      '/api/service-tickets',
      '/api/suppliers',
      '/api/reports',
      '/api/financial',
      '/api/whatsapp'
    ];

    // Setup routes are only for super admins - require proper tenant detection
    const superAdminOnlyRoutes = ['/api/setup', '/api/admin'];

    if (superAdminOnlyRoutes.some(route => req.path.startsWith(route))) {
      // Setup routes require super admin access
      if (subdomain !== 'admin' && subdomain !== 'main' && !req.path.startsWith('/api/admin')) {
        return res.status(403).json({ 
          error: 'Access denied',
          message: 'Setup dan admin panel hanya dapat diakses oleh super admin.'
        });
      }
      req.isSuperAdmin = true;
      return next();
    }

    if (skipRoutes.some(route => req.path.startsWith(route))) {
      return next();
    }

    // Find client by subdomain
    const client = await db
      .select()
      .from(clients)
      .where(eq(clients.subdomain, subdomain))
      .limit(1);

    if (!client.length) {
      return res.status(404).json({ 
        error: 'Tenant not found',
        subdomain,
        message: 'This subdomain is not registered or has been suspended.'
      });
    }

    const clientData = client[0];

    // Check client status
    if (clientData.status === 'suspended') {
      return res.status(403).json({ 
        error: 'Account suspended',
        message: 'This account has been suspended. Please contact support.'
      });
    }

    if (clientData.status === 'expired') {
      return res.status(402).json({ 
        error: 'Subscription expired',
        message: 'Your subscription has expired. Please renew to continue using the service.',
        renewUrl: `/renew?client=${clientData.id}`
      });
    }

    // Get active subscription
    const subscription = await db
      .select()
      .from(subscriptions)
      .where(
        and(
          eq(subscriptions.clientId, clientData.id),
          eq(subscriptions.paymentStatus, 'paid')
        )
      )
      .orderBy(subscriptions.endDate)
      .limit(1);

    // Check subscription expiry
    if (subscription.length && subscription[0].endDate < new Date()) {
      // Update client status to expired
      await db
        .update(clients)
        .set({ 
          status: 'expired',
          updatedAt: new Date()
        })
        .where(eq(clients.id, clientData.id));

      return res.status(402).json({ 
        error: 'Subscription expired',
        message: 'Your subscription has expired. Please renew to continue using the service.',
        renewUrl: `/renew?client=${clientData.id}`
      });
    }

    // Parse settings
    let settings = {};
    try {
      settings = clientData.settings ? JSON.parse(clientData.settings) : {};
    } catch (e) {
      console.error('Error parsing client settings:', e);
    }

    // Attach tenant info to request
    req.tenant = {
      id: clientData.id,
      subdomain: clientData.subdomain,
      name: clientData.name,
      status: clientData.status,
      subscription: subscription[0] || null,
      settings
    };

    console.log('Tenant loaded:', req.tenant.name, req.tenant.subdomain);
    next();

  } catch (error) {
    console.error('Tenant middleware error:', error);
    res.status(500).json({ 
      error: 'Tenant detection failed',
      message: 'Unable to determine tenant. Please try again.'
    });
  }
};

// Middleware to require valid tenant
export const requireTenant = (req: Request, res: Response, next: NextFunction) => {
  if (!req.tenant && !req.isSuperAdmin) {
    return res.status(400).json({ 
      error: 'No tenant context',
      message: 'This operation requires a valid tenant context.'
    });
  }
  next();
};

// Middleware to require super admin access
export const requireSuperAdmin = (req: Request, res: Response, next: NextFunction) => {
  console.log('RequireSuperAdmin middleware check:', { 
    isSuperAdmin: req.isSuperAdmin, 
    path: req.path,
    host: req.headers.host 
  });
  if (!req.isSuperAdmin) {
    console.log('Access denied - not super admin');
    return res.status(403).json({ 
      error: 'Super admin required',
      message: 'This operation requires super admin privileges.'
    });
  }
  console.log('Super admin access granted');
  next();
};

// Middleware to check subscription limits and feature access
export const checkSubscriptionLimits = (feature: string) => {
  return async (req: Request, res: Response, next: NextFunction) => {
    if (req.isSuperAdmin) {
      return next(); // Super admin bypasses limits
    }

    if (!req.tenant?.subscription) {
      return res.status(402).json({
        error: 'No active subscription',
        message: 'An active subscription is required for this feature.'
      });
    }

    try {
      // Get the subscription plan and check if feature is enabled
      const subscription = req.tenant.subscription;

      // If subscription is expired
      if (subscription.endDate && new Date(subscription.endDate) < new Date()) {
        return res.status(402).json({
          error: 'Subscription expired',
          message: 'Your subscription has expired. Please renew to continue using this feature.'
        });
      }

      // Check if feature is enabled in the plan
      if (subscription.plan?.features) {
        const planFeatures = JSON.parse(subscription.plan.features);

        if (!planFeatures.includes(feature)) {
          return res.status(403).json({
            error: 'Feature not available',
            message: `The '${feature}' feature is not available in your current plan. Please upgrade to access this feature.`,
            requiredFeature: feature,
            currentPlan: subscription.planName
          });
        }
      }

      // Check usage limits if applicable
      if (subscription.plan?.limits) {
        const planLimits = JSON.parse(subscription.plan.limits);

        // Example: Check user count limit
        if (feature === 'users' && planLimits.maxUsers) {
          // This would require a query to count current users
          // For now, we'll pass through but this can be extended
        }

        // Example: Check transaction limits
        if (feature === 'pos' && planLimits.maxTransactionsPerMonth) {
          // This would require checking current month's transactions
          // For now, we'll pass through but this can be extended
        }
      }

      next();
    } catch (error) {
      console.error('Error checking subscription limits:', error);
      res.status(500).json({
        error: 'Subscription check failed',
        message: 'Unable to verify subscription status.'
      });
    }
  };
};

// Middleware to check specific feature access
export const requireFeature = (featureName: string) => {
  return checkSubscriptionLimits(featureName);
};

// Helper function to check if a client has access to a feature
export const hasFeatureAccess = async (clientId: string, feature: string): Promise<boolean> => {
  try {
    // Import here to avoid circular dependencies
    const { db } = await import('../db');
    const { clients, subscriptions, plans } = await import('../../shared/saas-schema');
    const { eq, and } = await import('drizzle-orm');

    const result = await db
      .select({
        planFeatures: plans.features,
        subscriptionEnd: subscriptions.endDate
      })
      .from(clients)
      .leftJoin(subscriptions, and(
        eq(subscriptions.clientId, clients.id),
        eq(subscriptions.paymentStatus, 'paid')
      ))
      .leftJoin(plans, eq(plans.id, subscriptions.planId))
      .where(eq(clients.id, clientId))
      .limit(1);

    if (!result.length || !result[0].planFeatures) {
      return false; // No active subscription or plan
    }

    // Check if subscription is still active
    if (result[0].subscriptionEnd && new Date(result[0].subscriptionEnd) < new Date()) {
      return false; // Subscription expired
    }

    const planFeatures = JSON.parse(result[0].planFeatures);
    return planFeatures.includes(feature);
  } catch (error) {
    console.error('Error checking feature access:', error);
    return false;
  }
};

