import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { formatDateShort } from '@shared/utils/timezone';
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  Users, 
  Building2, 
  CreditCard, 
  TrendingUp, 
  Settings, 
  Eye,
  UserPlus,
  Trash2,
  Edit,
  CheckCircle,
  XCircle,
  Clock
} from "lucide-react";

interface Client {
  id: string;
  name: string;
  subdomain: string;
  email: string;
  status: 'active' | 'suspended' | 'expired' | 'trial';
  createdAt: string;
  trialEndsAt?: string;
  subscription?: {
    id: string;
    planName: string;
    paymentStatus: string;
    startDate: string;
    endDate: string;
    amount: number;
  };
}

interface Plan {
  id: string;
  name: string;
  description: string;
  price: number;
  features: string[];
  limits: any;
}

export default function AdminDashboard() {
  const { toast } = useToast();
  const [selectedClient, setSelectedClient] = useState<Client | null>(null);
  const [createClientOpen, setCreateClientOpen] = useState(false);
  const [newClient, setNewClient] = useState({
    name: '',
    subdomain: '',
    email: '',
    planId: ''
  });

  // Define stats interface with proper types
  interface AdminStats {
    totalClients?: number;
    newClientsThisMonth?: number;
    activeClients?: number;
    monthlyRevenue?: number;
    revenueGrowth?: number;
    expiringTrials?: number;
  }

  // Fetch dashboard stats
  const { data: stats } = useQuery<AdminStats>({
    queryKey: ['/api/admin/stats'],
  });

  // Fetch all clients
  const { data: clients, isLoading: clientsLoading } = useQuery<Client[]>({
    queryKey: ['/api/admin/clients'],
  });

  // Fetch subscription plans
  const { data: plans } = useQuery<Plan[]>({
    queryKey: ['/api/admin/plans'],
  });

  // Create client mutation
  const createClientMutation = useMutation({
    mutationFn: async (clientData: any) => {
      return await apiRequest('POST', '/api/admin/clients', clientData);
    },
    onSuccess: () => {
      toast({
        title: "Client berhasil dibuat",
        description: "Client baru telah ditambahkan ke sistem",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/clients'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/stats'] });
      setCreateClientOpen(false);
      setNewClient({ name: '', subdomain: '', email: '', planId: '' });
    },
    onError: (error: any) => {
      toast({
        title: "Gagal membuat client",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Update client status mutation
  const updateClientStatusMutation = useMutation({
    mutationFn: async ({ clientId, status }: { clientId: string; status: string }) => {
      return await apiRequest('PATCH', `/api/admin/clients/${clientId}/status`, { status });
    },
    onSuccess: () => {
      toast({
        title: "Status client berhasil diupdate",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/clients'] });
    },
    onError: (error: any) => {
      toast({
        title: "Gagal mengupdate status client",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const getStatusBadge = (status: string) => {
    const variants = {
      active: { variant: "default" as const, label: "Aktif", icon: CheckCircle },
      trial: { variant: "secondary" as const, label: "Trial", icon: Clock },
      expired: { variant: "destructive" as const, label: "Expired", icon: XCircle },
      suspended: { variant: "outline" as const, label: "Suspended", icon: XCircle }
    };

    const config = variants[status as keyof typeof variants] || variants.active;
    const Icon = config.icon;

    return (
      <Badge variant={config.variant} className="flex items-center gap-1">
        <Icon className="h-3 w-3" />
        {config.label}
      </Badge>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Admin Dashboard</h1>
            <p className="text-gray-600">Kelola client dan langganan SaaS</p>
          </div>
          <Dialog open={createClientOpen} onOpenChange={setCreateClientOpen}>
            <DialogTrigger asChild>
              <Button data-testid="button-create-client">
                <UserPlus className="h-4 w-4 mr-2" />
                Tambah Client
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Tambah Client Baru</DialogTitle>
                <DialogDescription>
                  Buat client baru dengan langganan
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="client-name">Nama Client</Label>
                  <Input
                    id="client-name"
                    data-testid="input-client-name"
                    value={newClient.name}
                    onChange={(e) => setNewClient(prev => ({ ...prev, name: e.target.value }))}
                    placeholder="PT. Contoh Perusahaan"
                  />
                </div>
                <div>
                  <Label htmlFor="client-subdomain">Subdomain</Label>
                  <Input
                    id="client-subdomain"
                    data-testid="input-client-subdomain"
                    value={newClient.subdomain}
                    onChange={(e) => setNewClient(prev => ({ ...prev, subdomain: e.target.value }))}
                    placeholder="contoh"
                  />
                  <p className="text-sm text-gray-500 mt-1">
                    Client akan mengakses di: {newClient.subdomain}.yourdomain.com
                  </p>
                </div>
                <div>
                  <Label htmlFor="client-email">Email</Label>
                  <Input
                    id="client-email"
                    data-testid="input-client-email"
                    type="email"
                    value={newClient.email}
                    onChange={(e) => setNewClient(prev => ({ ...prev, email: e.target.value }))}
                    placeholder="admin@contoh.com"
                  />
                </div>
                <div>
                  <Label htmlFor="client-plan">Paket Langganan</Label>
                  <Select onValueChange={(value) => setNewClient(prev => ({ ...prev, planId: value }))}>
                    <SelectTrigger data-testid="select-client-plan">
                      <SelectValue placeholder="Pilih paket" />
                    </SelectTrigger>
                    <SelectContent>
                      {plans?.map((plan) => (
                        <SelectItem key={plan.id} value={plan.id}>
                          {plan.name} - Rp {plan.price.toLocaleString()}/bulan
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>
              <DialogFooter>
                <Button 
                  data-testid="button-submit-client"
                  onClick={() => createClientMutation.mutate(newClient)}
                  disabled={createClientMutation.isPending}
                >
                  {createClientMutation.isPending ? 'Membuat...' : 'Buat Client'}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Client</CardTitle>
              <Users className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="stat-total-clients">
                {stats?.totalClients ?? 0}
              </div>
              <p className="text-xs text-muted-foreground">
                +{stats?.newClientsThisMonth ?? 0} bulan ini
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Client Aktif</CardTitle>
              <Building2 className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600" data-testid="stat-active-clients">
                {stats?.activeClients ?? 0}
              </div>
              <p className="text-xs text-muted-foreground">
                {stats?.activeClients && stats?.totalClients ? ((stats.activeClients / stats.totalClients) * 100).toFixed(1) : '0'}% dari total
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Revenue Bulan Ini</CardTitle>
              <CreditCard className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="stat-monthly-revenue">
                Rp {(stats?.monthlyRevenue ?? 0).toLocaleString()}
              </div>
              <p className="text-xs text-muted-foreground">
                +{stats?.revenueGrowth ?? 0}% dari bulan lalu
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Trial yang Berakhir</CardTitle>
              <TrendingUp className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-yellow-600" data-testid="stat-expiring-trials">
                {stats?.expiringTrials ?? 0}
              </div>
              <p className="text-xs text-muted-foreground">
                7 hari ke depan
              </p>
            </CardContent>
          </Card>
        </div>

        {/* Main Content Tabs */}
        <Tabs defaultValue="clients" className="space-y-4">
          <TabsList>
            <TabsTrigger value="clients">Client Management</TabsTrigger>
            <TabsTrigger value="subscriptions">Langganan</TabsTrigger>
            <TabsTrigger value="plans">Paket Langganan</TabsTrigger>
            <TabsTrigger value="settings">Pengaturan</TabsTrigger>
          </TabsList>

          <TabsContent value="clients" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Daftar Client</CardTitle>
                <CardDescription>
                  Kelola semua client dan status langganan mereka
                </CardDescription>
              </CardHeader>
              <CardContent>
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Client</TableHead>
                      <TableHead>Subdomain</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Paket</TableHead>
                      <TableHead>Terdaftar</TableHead>
                      <TableHead>Aksi</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {clients?.map((client) => (
                      <TableRow key={client.id}>
                        <TableCell>
                          <div>
                            <div className="font-medium">{client.name}</div>
                            <div className="text-sm text-gray-500">{client.email}</div>
                          </div>
                        </TableCell>
                        <TableCell>
                          <code className="text-sm bg-gray-100 px-2 py-1 rounded">
                            {client.subdomain}
                          </code>
                        </TableCell>
                        <TableCell>
                          {getStatusBadge(client.status)}
                        </TableCell>
                        <TableCell>
                          {client.subscription ? (
                            <div>
                              <div className="font-medium">{client.subscription.planName}</div>
                              <div className="text-sm text-gray-500">
                                Rp {client.subscription.amount.toLocaleString()}/bulan
                              </div>
                            </div>
                          ) : (
                            <span className="text-gray-400">No subscription</span>
                          )}
                        </TableCell>
                        <TableCell>
                          {formatDateShort(client.createdAt)}
                        </TableCell>
                        <TableCell>
                          <div className="flex gap-2">
                            <Button
                              variant="outline"
                              size="sm"
                              data-testid={`button-view-client-${client.id}`}
                              onClick={() => setSelectedClient(client)}
                            >
                              <Eye className="h-4 w-4" />
                            </Button>
                            <Select onValueChange={(status) => 
                              updateClientStatusMutation.mutate({ clientId: client.id, status })
                            }>
                              <SelectTrigger className="w-32">
                                <SelectValue placeholder="Status" />
                              </SelectTrigger>
                              <SelectContent>
                                <SelectItem value="active">Aktifkan</SelectItem>
                                <SelectItem value="suspended">Suspend</SelectItem>
                                <SelectItem value="expired">Expire</SelectItem>
                              </SelectContent>
                            </Select>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="subscriptions" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Monitoring Langganan</CardTitle>
                <CardDescription>
                  Monitor status pembayaran dan perpanjangan langganan
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="text-center py-8 text-gray-500">
                  <CreditCard className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  Feature monitoring langganan akan ditambahkan di sini
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="plans" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Paket Langganan</CardTitle>
                <CardDescription>
                  Kelola paket dan harga langganan
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  {plans?.map((plan) => (
                    <Card key={plan.id} className="border-2">
                      <CardHeader>
                        <CardTitle className="text-lg">{plan.name}</CardTitle>
                        <CardDescription>{plan.description}</CardDescription>
                        <div className="text-2xl font-bold">
                          Rp {plan.price.toLocaleString()}
                          <span className="text-sm font-normal text-gray-500">/bulan</span>
                        </div>
                      </CardHeader>
                      <CardContent>
                        <ul className="space-y-2 text-sm">
                          {(Array.isArray(plan.features) ? plan.features : JSON.parse(plan.features || '[]')).map((feature: string, index: number) => (
                            <li key={index} className="flex items-center gap-2">
                              <CheckCircle className="h-4 w-4 text-green-500" />
                              {feature}
                            </li>
                          ))}
                        </ul>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="settings" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Pengaturan Sistem</CardTitle>
                <CardDescription>
                  Konfigurasi umum untuk sistem SaaS
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="text-center py-8 text-gray-500">
                  <Settings className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  Pengaturan sistem akan ditambahkan di sini
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/client-onboarding.tsx.content:
import { useState, useEffect } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { 
  CheckCircle, 
  Circle, 
  Store, 
  User, 
  Settings, 
  ArrowRight, 
  Loader2,
  Rocket,
  Users,
  Building2
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface OnboardingStatus {
  completed: boolean;
  currentStep: number;
  clientInfo?: {
    name: string;
    subdomain: string;
    planName: string;
    trialEndsAt: string;
  };
}

export default function ClientOnboarding() {
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [currentStep, setCurrentStep] = useState(1);
  const [onboardingData, setOnboardingData] = useState({
    storeName: '',
    storeAddress: '',
    storePhone: '',
    storeEmail: '',
    adminUsername: '',
    adminPassword: '',
    adminEmail: '',
    adminFirstName: '',
    adminLastName: '',
  });

  // Check onboarding status
  const { data: onboardingStatus, isLoading } = useQuery<OnboardingStatus>({
    queryKey: ['/api/client/onboarding/status'],
    refetchInterval: false,
  });

  useEffect(() => {
    // Redirect if onboarding is already completed
    if (onboardingStatus?.completed) {
      toast({
        title: "Setup Selesai",
        description: "Aplikasi sudah siap digunakan. Mengarahkan ke dashboard...",
      });
      setTimeout(() => setLocation('/dashboard'), 2000);
    }
  }, [onboardingStatus, setLocation, toast]);

  // Store setup mutation
  const storeSetupMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest('POST', '/api/client/onboarding/store', data);
      return response;
    },
    onSuccess: () => {
      toast({
        title: "Konfigurasi Toko Berhasil",
        description: "Informasi toko telah disimpan",
      });
      setCurrentStep(2);
    },
    onError: (error: any) => {
      toast({
        title: "Gagal Menyimpan Konfigurasi Toko",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Admin user creation mutation
  const adminSetupMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest('POST', '/api/client/onboarding/admin', data);
      return response;
    },
    onSuccess: () => {
      toast({
        title: "Admin User Berhasil Dibuat",
        description: "Account administrator telah dibuat",
      });
      setCurrentStep(3);
    },
    onError: (error: any) => {
      toast({
        title: "Gagal Membuat Admin User",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Complete onboarding mutation
  const completeOnboardingMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/client/onboarding/complete', {});
      return response;
    },
    onSuccess: () => {
      toast({
        title: "Onboarding Selesai!",
        description: "Selamat datang di LaptopPOS! Aplikasi siap digunakan.",
      });
      setTimeout(() => setLocation('/dashboard'), 2000);
    },
    onError: (error: any) => {
      toast({
        title: "Gagal Menyelesaikan Onboarding",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleStoreSetup = () => {
    const { storeName, storeAddress, storePhone, storeEmail } = onboardingData;

    if (!storeName) {
      toast({
        title: "Data Tidak Lengkap",
        description: "Nama toko harus diisi",
        variant: "destructive",
      });
      return;
    }

    storeSetupMutation.mutate({
      name: storeName,
      address: storeAddress,
      phone: storePhone,
      email: storeEmail,
    });
  };

  const handleAdminSetup = () => {
    const { adminUsername, adminPassword, adminEmail, adminFirstName, adminLastName } = onboardingData;

    if (!adminUsername || !adminPassword || !adminEmail) {
      toast({
        title: "Data Tidak Lengkap",
        description: "Username, password, dan email harus diisi",
        variant: "destructive",
      });
      return;
    }

    adminSetupMutation.mutate({
      username: adminUsername,
      password: adminPassword,
      email: adminEmail,
      firstName: adminFirstName,
      lastName: adminLastName,
    });
  };

  const getStepIcon = (step: number, currentStep: number) => {
    if (step < currentStep) {
      return <CheckCircle className="h-6 w-6 text-green-500" />;
    } else if (step === currentStep) {
      return <Circle className="h-6 w-6 text-blue-500 fill-blue-500" />;
    } else {
      return <Circle className="h-6 w-6 text-gray-300" />;
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" />
          <p className="text-gray-600">Memuat status onboarding...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="w-full max-w-4xl">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <Rocket className="h-8 w-8 text-blue-600 mr-2" />
            <h1 className="text-3xl font-bold text-gray-900">Selamat Datang!</h1>
          </div>
          <p className="text-gray-600 text-lg">
            Mari siapkan aplikasi LaptopPOS untuk {onboardingStatus?.clientInfo?.name}
          </p>
          {onboardingStatus?.clientInfo && (
            <div className="mt-4 flex items-center justify-center gap-4 text-sm text-gray-500">
              <span className="flex items-center gap-1">
                <Building2 className="h-4 w-4" />
                {onboardingStatus.clientInfo.subdomain}.yourdomain.com
              </span>
              <span>â€¢</span>
              <span className="flex items-center gap-1">
                <Rocket className="h-4 w-4" />
                {onboardingStatus.clientInfo.planName}
              </span>
              <span>â€¢</span>
              <span>
                Trial hingga {new Date(onboardingStatus.clientInfo.trialEndsAt).toLocaleDateString('id-ID')}
              </span>
            </div>
          )}
        </div>

        {/* Progress Steps */}
        <div className="flex items-center justify-center mb-8">
          <div className="flex items-center space-x-8">
            <div className="flex items-center">
              {getStepIcon(1, currentStep)}
              <span className={`ml-2 text-sm font-medium ${currentStep >= 1 ? 'text-gray-900' : 'text-gray-400'}`}>
                Konfigurasi Toko
              </span>
            </div>
            <ArrowRight className="h-4 w-4 text-gray-400" />
            <div className="flex items-center">
              {getStepIcon(2, currentStep)}
              <span className={`ml-2 text-sm font-medium ${currentStep >= 2 ? 'text-gray-900' : 'text-gray-400'}`}>
                Admin User
              </span>
            </div>
            <ArrowRight className="h-4 w-4 text-gray-400" />
            <div className="flex items-center">
              {getStepIcon(3, currentStep)}
              <span className={`ml-2 text-sm font-medium ${currentStep >= 3 ? 'text-gray-900' : 'text-gray-400'}`}>
                Selesai
              </span>
            </div>
          </div>
        </div>

        {/* Progress Bar */}
        <div className="mb-8">
          <Progress value={(currentStep / 3) * 100} className="h-2" />
        </div>

        {/* Step Content */}
        <div className="space-y-6">
          {currentStep === 1 && (
            <Card className="shadow-lg">
              <CardHeader className="text-center">
                <div className="flex items-center justify-center mb-4">
                  <Store className="h-8 w-8 text-blue-600" />
                </div>
                <CardTitle>Konfigurasi Toko</CardTitle>
                <CardDescription>
                  Atur informasi dasar toko Anda
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="store-name">Nama Toko *</Label>
                    <Input
                      id="store-name"
                      data-testid="input-store-name"
                      value={onboardingData.storeName}
                      onChange={(e) => setOnboardingData(prev => ({ ...prev, storeName: e.target.value }))}
                      placeholder="Nama toko Anda"
                    />
                  </div>
                  <div>
                    <Label htmlFor="store-email">Email Toko</Label>
                    <Input
                      id="store-email"
                      data-testid="input-store-email"
                      type="email"
                      value={onboardingData.storeEmail}
                      onChange={(e) => setOnboardingData(prev => ({ ...prev, storeEmail: e.target.value }))}
                      placeholder="email@toko.com"
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="store-address">Alamat Toko</Label>
                  <Input
                    id="store-address"
                    data-testid="input-store-address"
                    value={onboardingData.storeAddress}
                    onChange={(e) => setOnboardingData(prev => ({ ...prev, storeAddress: e.target.value }))}
                    placeholder="Alamat lengkap toko"
                  />
                </div>
                <div>
                  <Label htmlFor="store-phone">Nomor Telepon</Label>
                  <Input
                    id="store-phone"
                    data-testid="input-store-phone"
                    value={onboardingData.storePhone}
                    onChange={(e) => setOnboardingData(prev => ({ ...prev, storePhone: e.target.value }))}
                    placeholder="08123456789"
                  />
                </div>
                <Button 
                  data-testid="button-next-store"
                  onClick={handleStoreSetup}
                  disabled={storeSetupMutation.isPending}
                  className="w-full"
                >
                  {storeSetupMutation.isPending ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Menyimpan...
                    </>
                  ) : (
                    <>
                      Lanjutkan
                      <ArrowRight className="ml-2 h-4 w-4" />
                    </>
                  )}
                </Button>
              </CardContent>
            </Card>
          )}

          {currentStep === 2 && (
            <Card className="shadow-lg">
              <CardHeader className="text-center">
                <div className="flex items-center justify-center mb-4">
                  <User className="h-8 w-8 text-blue-600" />
                </div>
                <CardTitle>Buat Admin User</CardTitle>
                <CardDescription>
                  Buat account administrator untuk mengelola sistem
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="admin-username">Username *</Label>
                    <Input
                      id="admin-username"
                      data-testid="input-admin-username"
                      value={onboardingData.adminUsername}
                      onChange={(e) => setOnboardingData(prev => ({ ...prev, adminUsername: e.target.value }))}
                      placeholder="admin"
                    />
                  </div>
                  <div>
                    <Label htmlFor="admin-email">Email *</Label>
                    <Input
                      id="admin-email"
                      data-testid="input-admin-email"
                      type="email"
                      value={onboardingData.adminEmail}
                      onChange={(e) => setOnboardingData(prev => ({ ...prev, adminEmail: e.target.value }))}
                      placeholder="admin@email.com"
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="admin-password">Password *</Label>
                  <Input
                    id="admin-password"
                    data-testid="input-admin-password"
                    type="password"
                    value={onboardingData.adminPassword}
                    onChange={(e) => setOnboardingData(prev => ({ ...prev, adminPassword: e.target.value }))}
                    placeholder="Password yang kuat"
                  />
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="admin-first-name">Nama Depan</Label>
                    <Input
                      id="admin-first-name"
                      data-testid="input-admin-first-name"
                      value={onboardingData.adminFirstName}
                      onChange={(e) => setOnboardingData(prev => ({ ...prev, adminFirstName: e.target.value }))}
                      placeholder="Nama depan"
                    />
                  </div>
                  <div>
                    <Label htmlFor="admin-last-name">Nama Belakang</Label>
                    <Input
                      id="admin-last-name"
                      data-testid="input-admin-last-name"
                      value={onboardingData.adminLastName}
                      onChange={(e) => setOnboardingData(prev => ({ ...prev, adminLastName: e.target.value }))}
                      placeholder="Nama belakang"
                    />
                  </div>
                </div>
                <div className="flex gap-3">
                  <Button 
                    variant="outline" 
                    onClick={() => setCurrentStep(1)}
                    className="flex-1"
                  >
                    Kembali
                  </Button>
                  <Button 
                    data-testid="button-next-admin"
                    onClick={handleAdminSetup}
                    disabled={adminSetupMutation.isPending}
                    className="flex-1"
                  >
                    {adminSetupMutation.isPending ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Membuat...
                      </>
                    ) : (
                      <>
                        Buat Admin
                        <ArrowRight className="ml-2 h-4 w-4" />
                      </>
                    )}
                  </Button>
                </div>
              </CardContent>
            </Card>
          )}

          {currentStep === 3 && (
            <Card className="shadow-lg">
              <CardHeader className="text-center">
                <div className="flex items-center justify-center mb-4">
                  <CheckCircle className="h-12 w-12 text-green-500" />
                </div>
                <CardTitle className="text-green-600">Setup Hampir Selesai!</CardTitle>
                <CardDescription>
                  Aplikasi LaptopPOS siap digunakan untuk {onboardingStatus?.clientInfo?.name}
                </CardDescription>
              </CardHeader>
              <CardContent className="text-center space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                  <div className="flex flex-col items-center p-4 bg-green-50 rounded-lg">
                    <Store className="h-8 w-8 text-green-600 mb-2" />
                    <p className="font-medium">Toko Dikonfigurasi</p>
                    <p className="text-gray-600">Informasi toko tersimpan</p>
                  </div>
                  <div className="flex flex-col items-center p-4 bg-blue-50 rounded-lg">
                    <Users className="h-8 w-8 text-blue-600 mb-2" />
                    <p className="font-medium">Admin Dibuat</p>
                    <p className="text-gray-600">Account administrator siap</p>
                  </div>
                  <div className="flex flex-col items-center p-4 bg-purple-50 rounded-lg">
                    <Settings className="h-8 w-8 text-purple-600 mb-2" />
                    <p className="font-medium">Sistem Siap</p>
                    <p className="text-gray-600">Siap untuk digunakan</p>
                  </div>
                </div>

                <Button 
                  data-testid="button-complete-onboarding"
                  onClick={() => completeOnboardingMutation.mutate()}
                  disabled={completeOnboardingMutation.isPending}
                  size="lg"
                  className="w-full"
                >
                  {completeOnboardingMutation.isPending ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Menyelesaikan Setup...
                    </>
                  ) : (
                    <>
                      <Rocket className="mr-2 h-4 w-4" />
                      Mulai Menggunakan LaptopPOS
                    </>
                  )}
                </Button>
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}

server/routes/client.ts.content:
import { Router } from 'express';
import { z } from 'zod';
import { db } from '../db';
import { users, storeConfig } from '../../shared/schema';
import { clients } from '../../shared/saas-schema';
import { eq } from 'drizzle-orm';
import { requireTenant } from '../middleware/tenant';
import bcrypt from 'bcryptjs';

const router = Router();

// All client routes require valid tenant context
router.use(requireTenant);

// Client onboarding status
router.get('/onboarding/status', async (req: any, res) => {
  try {
    const clientId = req.tenant?.id;

    if (!clientId) {
      return res.status(400).json({ message: 'No client context found' });
    }

    // Get client info
    const [client] = await db
      .select()
      .from(clients)
      .where(eq(clients.id, clientId))
      .limit(1);

    if (!client) {
      return res.status(404).json({ message: 'Client not found' });
    }

    // Check if store config exists
    const [config] = await db
      .select()
      .from(storeConfig)
      .where(eq(storeConfig.clientId, clientId))
      .limit(1);

    // Check if admin user exists
    const [adminUser] = await db
      .select()
      .from(users)
      .where(eq(users.clientId, clientId))
      .limit(1);

    const completed = Boolean(config && adminUser && config.setupCompleted);

    res.json({
      completed,
      currentStep: completed ? 3 : (config ? (adminUser ? 3 : 2) : 1),
      clientInfo: {
        name: client.name,
        subdomain: client.subdomain,
        planName: client.settings ? JSON.parse(client.settings).planName : 'Trial',
        trialEndsAt: client.trialEndsAt
      }
    });
  } catch (error) {
    console.error('Error checking onboarding status:', error);
    res.status(500).json({ message: 'Failed to check onboarding status' });
  }
});

// Store setup for client onboarding
const storeSetupSchema = z.object({
  name: z.string().min(1, 'Store name is required'),
  address: z.string().optional(),
  phone: z.string().optional(),
  email: z.string().email().optional().or(z.literal(''))
});

router.post('/onboarding/store', async (req: any, res) => {
  try {
    const clientId = req.tenant?.id;

    if (!clientId) {
      return res.status(400).json({ message: 'No client context found' });
    }

    const { name, address, phone, email } = storeSetupSchema.parse(req.body);

    // Check if store config already exists
    const [existingConfig] = await db
      .select()
      .from(storeConfig)
      .where(eq(storeConfig.clientId, clientId))
      .limit(1);

    if (existingConfig) {
      // Update existing config
      const [updatedConfig] = await db
        .update(storeConfig)
        .set({
          name,
          address: address || '',
          phone: phone || '',
          email: email || '',
          updatedAt: new Date()
        })
        .where(eq(storeConfig.id, existingConfig.id))
        .returning();

      res.json({
        success: true,
        message: 'Store configuration updated successfully',
        config: updatedConfig
      });
    } else {
      // Create new config
      const [newConfig] = await db
        .insert(storeConfig)
        .values({
          clientId,
          name,
          address: address || '',
          phone: phone || '',
          email: email || '',
          taxRate: '11.00',
          setupCompleted: false
        })
        .returning();

      res.json({
        success: true,
        message: 'Store configuration created successfully',
        config: newConfig
      });
    }
  } catch (error) {
    console.error('Error setting up store:', error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        message: 'Validation error',
        errors: error.errors 
      });
    }
    res.status(500).json({ message: 'Failed to setup store configuration' });
  }
});

// Admin user creation for client onboarding
const adminSetupSchema = z.object({
  username: z.string().min(1, 'Username is required'),
  password: z.string().min(6, 'Password must be at least 6 characters'),
  email: z.string().email('Valid email is required'),
  firstName: z.string().optional(),
  lastName: z.string().optional()
});

router.post('/onboarding/admin', async (req: any, res) => {
  try {
    const clientId = req.tenant?.id;

    if (!clientId) {
      return res.status(400).json({ message: 'No client context found' });
    }

    const { username, password, email, firstName, lastName } = adminSetupSchema.parse(req.body);

    // Check if admin user already exists for this client
    const [existingUser] = await db
      .select()
      .from(users)
      .where(eq(users.clientId, clientId))
      .limit(1);

    if (existingUser) {
      return res.status(400).json({ message: 'Admin user already exists for this client' });
    }

    // Hash password
    const hashedPassword = await bcrypt.hash(password, 10);

    // Create admin user
    const [newUser] = await db
      .insert(users)
      .values({
        clientId,
        username,
        password: hashedPassword,
        email,
        firstName: firstName || 'Admin',
        lastName: lastName || 'User',
        role: 'admin',
        isActive: true
      })
      .returning();

    // Remove password from response
    const { password: _, ...userWithoutPassword } = newUser;

    res.json({
      success: true,
      message: 'Admin user created successfully',
      user: userWithoutPassword
    });
  } catch (error) {
    console.error('Error creating admin user:', error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        message: 'Validation error',
        errors: error.errors 
      });
    }
    res.status(500).json({ message: 'Failed to create admin user' });
  }
});

// Complete onboarding
router.post('/onboarding/complete', async (req: any, res) => {
  try {
    const clientId = req.tenant?.id;

    if (!clientId) {
      return res.status(400).json({ message: 'No client context found' });
    }

    // Update store config to mark setup as completed
    const [updatedConfig] = await db
      .update(storeConfig)
      .set({
        setupCompleted: true,
        updatedAt: new Date()
      })
      .where(eq(storeConfig.clientId, clientId))
      .returning();

    if (!updatedConfig) {
      return res.status(400).json({ message: 'Store configuration not found' });
    }

    res.json({
      success: true,
      message: 'Onboarding completed successfully! Welcome to LaptopPOS!'
    });
  } catch (error) {
    console.error('Error completing onboarding:', error);
    res.status(500).json({ message: 'Failed to complete onboarding' });
  }
});

export default router;

